import { useState, useEffect } from "react";
import { State } from "country-state-city";

function SelectState(props) {
  const [states, setStates] = useState([]);
  const position = {
    container: "flex flex-col w-full",
    wrapper: "flex justify-between w-full *:p-4 my-2",
    item: "w-11/12",
  };
  const design = {
    wrapper: "border border-gray-300 rounded-md hover:border-gray-700",
    item: `${
      props.value === "" ? "text-gray-400" : ""
    } outline-none appearance-none`,
  };

  useEffect(() => {
    props.country
      ? setStates(State.getStatesOfCountry(props.country))
      : setStates([]);
  }, [props.country]);

  return (
    <>
      <div className={position.container}>
        <div className={`${position.wrapper} ${design.wrapper}`}>
          <select
            className={`${position.item} ${design.item}`}
            name={props.name}
            value={props.value}
            onChange={props.onChange}
          >
            <option value="">State*</option>
            {states.map((item) => (
              <option key={item.isoCode} value={item.isoCode}>
                {item.name}
              </option>
            ))}
          </select>
        </div>
      </div>
      {props.errName && <p className={design.text}>{props.errName}</p>}
    </>
  );
}

export default SelectState;
