import { useState, useEffect } from "react";
import axios from "axios";
import Layout from "../layout";
import Filter from "../components/filter";

function Collection(props) {
  const [products, setProducts] = useState([]);

  useEffect((_) => {
    axios
      .post("/products", { category: props.name })
      .then((res) => setProducts(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <Layout>
      <div className="max-[767px]:w-11/12 min-[768px]:w-4/5 pt-20 m-auto">
        <h2 className="text-xl">
          {props.name} Shoes ({products.length > 0 ? products.length : "0"})
        </h2>
        <div className="mt-4 *:inline-block *:align-top">
          <div className="max-[767px]:w-1/4 min-[768px]:w-2/12">
            <Filter />
          </div>
          <div className="w-4/6 mx-9">
            {products.length > 0
              ? products.map((product) => (
                  <a
                    key={product._id}
                    className="flex-shrink-0"
                    style={{ width: "30vw" }}
                    href={`/product/${product.slug}`}
                  >
                    <div className="flex flex-col">
                      <img
                        key={product._id}
                        className="bg-gray-100 px-5 object-contain w-full border border-transparent rounded-lg"
                        style={{ height: "45vh" }}
                        src={product.image}
                        alt={product.name}
                      />
                      <p className="font-medium mt-2 text-gray-900">
                        {product.name}
                      </p>
                      <p className="text-gray-500">{product.category} Shoes</p>
                      <p className="font-medium mt-2 text-gray-900">
                        ${product.price}.00
                      </p>
                    </div>
                  </a>
                ))
              : "No products found"}
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Collection;
