import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Layout from "../layout";
import Slider from "../components/slider";

function Favorites() {
  const favorites = JSON.parse(sessionStorage.getItem("favorite")) || [];
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .post("/products")
      .then((res) => setProducts(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <Layout>
      <div className="w-10/12 py-10 m-auto">
        <h3 className="font-medium text-2xl text-gray-700 mb-5">Favorites</h3>
        <div className="pt-28 pb-10">
          {favorites.length ? (
            favorites.map((product) => (
              <div
                key={product._id}
                onClick={() => navigate(`/product/${product.slug}`)}
                className="flex flex-col text-sm sm:text-base mt-5"
              >
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full px-5 h-64 object-contain mb-2 bg-gray-100 border border-transparent rounded-lg"
                />
                <p className="font-medium text-gray-900">{product.name}</p>
                <p className="text-gray-500">{product.category} Shoes</p>
                <p className="font-medium text-gray-900 mt-1">
                  {product.discount && product.discount > 0 ? (
                    <>
                      <span>
                        {"$" +
                          (
                            product.price -
                            (product.price * product.discount) / 100
                          ).toFixed(2)}
                      </span>
                      <span className="text-gray-300 px-2 line-through">
                        ${product.price.toFixed(2)}
                      </span>
                    </>
                  ) : (
                    <span>${product.price.toFixed(2)}</span>
                  )}
                </p>
              </div>
            ))
          ) : (
            <p className="font-medium text-center">
              You can find items you've favorited saved here.
            </p>
          )}
        </div>
        <Slider products={products} heading="Discover Your Next Favorites" />
      </div>
    </Layout>
  );
}

export default Favorites;
