import { useEffect, useState } from "react";
import { Checkmark } from "react-checkmark";
import Layout from "../components/layout/layout";
import { createOrder } from "../server";

function Success() {
  const position = {
    container: "flex flex-col gap-4 my-5",
    wrapper: "grid grid-cols-2 gap-5 w-3/4 m-auto py-10",
  };
  const design = { container: "text-center" };

  const [order, setOrder] = useState(
    JSON.parse(sessionStorage.getItem("order")) || {},
  );

  useEffect(() => {
    const cart = JSON.parse(sessionStorage.getItem("cart"));
    const user = JSON.parse(sessionStorage.getItem("user"));
    const shipping = JSON.parse(sessionStorage.getItem("shipping"));
    const payment = JSON.parse(sessionStorage.getItem("payment"));

    !order._id &&
      createOrder(
        {
          cart: cart.map((item) => item._id),
          user: user._id,
          shipping: shipping._id,
          payment: payment._id,
        },
        setOrder,
      );
  }, [order]);

  return (
    <Layout>
      <div className={`${position.container} ${design.container}`}>
        <Checkmark size="8rem" />
        <Heading text="Order placed successfully!" />
        <Text text={`Order ID #` + order._id} />
        <Separator />
        <Subheading text={`Total: $` + order.payment?.amount} />
        <Separator />
        <div className={position.wrapper}>
          <div>
            <Subheading text="Your Information" />
            <Text text={`${order.user.fname} ${order.user.lname}`} />
            <Text text={order.user.email} type="lowercase" />
            <Text text={order.user.phone} />
          </div>
          <div>
            <Subheading text="Payment Information" />
            <Text text={order.payment.mode} />
          </div>
          <div>
            <Subheading text="Delivery Information" />
            <Text text={order.user.address} />
          </div>
          <div>
            <Subheading text="Delivery Information" />
            <Text text={order.shipping.date} />
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Success;

function Subheading(props) {
  const position = "my-2";
  const design = "font-medium text-lg";

  return <h3 className={`${position} ${design}`}>{props.text}</h3>;
}

function Heading(props) {
  const position = "pt-10";
  const design = "font-medium text-2xl";

  return <h2 className={`${position} ${design}`}>{props.text}</h2>;
}

function Text(props) {
  const position = "my-2";
  const design = props.type === "lowercase" ? "lowercase" : "capitalize";

  return <p className={`${position} ${design}`}>{props.text}</p>;
}

function Separator() {
  const position = "h-px w-1/4 m-auto";
  const design = "bg-gray-200";

  return <hr className={`${position} ${design}`} />;
}
