import { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import Layout from "../components/layout/layout";
import CardHorizontal from "../components/cardHorizontal";
import Input from "../components/input";
import Button from "../components/button";
import FilterIcon from "../icons/filter";
import LinkIcon from "../icons/link";
import PrivacyIcon from "../icons/privacy";
import ShareIcon from "../icons/share";
import UserIcon from "../icons/user";
import ChatIcon from "../icons/chat";
import HouseIcon from "../icons/house";
import { getOrder, getAddress, createAddress, updateUser } from "../server";
import { membershipDate, shippingDate, validate } from "../helper";

function Dashboard() {
  const user = JSON.parse(sessionStorage.getItem("user")) || {};
  const [show, setShow] = useState("Profile");
  const position = {
    container: "sm:mx-10",
    contentWrapper: "my-10",
    separator: "h-0.5 my-4",
    topbarContainer:
      "flex flex-col gap-10 sm:flex-row sm:gap-40 sm:items-center",
    topbarWrapper: "flex gap-5",
  };
  const design = {
    active: "text-gray-500",
    heading: "text-2xl",
    separator: "bg-gray-300",
    topbarWrapper: "font-medium *:cursor-pointer",
  };

  function isActive(e) {
    const siblings = e.target.parentNode.children;
    [...siblings].forEach((sibling) => sibling.classList.remove(design.active));
    e.target.classList.add(design.active);
    setShow(e.target.innerHTML);
  }

  return (
    <Layout>
      <div className={position.container}>
        <div className={position.topbarContainer}>
          <p className={design.heading}>{show}</p>
          <div className={`${position.topbarWrapper} ${design.topbarWrapper}`}>
            <p className={design.active} onClick={(e) => isActive(e)}>
              Profile
            </p>
            <p onClick={(e) => isActive(e)}>Inbox</p>
            <p onClick={(e) => isActive(e)}>Orders</p>
            <p onClick={(e) => isActive(e)}>Favourites</p>
            <p onClick={(e) => isActive(e)}>Settings</p>
          </div>
        </div>
        <hr className={`${position.separator} ${design.separator}`} />
        <div className={position.contentWrapper}>
          {show === "Profile" && <Profile user={user} />}
          {show === "Orders" && <Orders user={user} />}
          {show === "Settings" && <Settings user={user} />}
        </div>
      </div>
    </Layout>
  );
}

export default Dashboard;

function Profile(props) {
  const position = {
    container: "flex flex-col gap-2 justify-center items-center",
    image: "flex items-center justify-center w-28 h-28 mt-10",
  };
  const design = {
    heading: "text-4xl",
    text: "text-gray-500",
    image: "bg-gray-200 rounded-full",
  };

  return props.user ? (
    <div className={position.container}>
      <div className={`${position.image} ${design.image}`}>
        <UserIcon />
      </div>
      <h3 className={design.heading}>
        {props.user.fname} {props.user.lname}
      </h3>
      <p className={design.text}>
        Member Since {membershipDate(props.user.created)}
      </p>
    </div>
  ) : (
    <p className={design.text}>You're not logged in yet</p>
  );
}

function Orders(props) {
  const [order, setOrder] = useState({});
  const position = {
    cardContainer: "grid grid-cols-1 md:grid-cols-2",
  };
  const design = {
    text: "text-gray-500",
  };

  useEffect(() => {
    getOrder({ user: props.user?._id }, setOrder);
  }, [props.user]);

  return Object.keys(order).length ? (
    <div className={position.cardContainer}>
      {order.cart.map((item) => (
        <CardHorizontal
          key={item.product._id}
          badge={
            order.shipping.status === "In Progress"
              ? `Delivery by ${shippingDate(order.shipping.date)}`
              : "Shipped"
          }
          cart={item}
          product={item.product}
          buttons={
            order.shipping.status === "In Progress"
              ? [
                  { name: "Track Delivery" },
                  { name: "Start Return", type: "light" },
                ]
              : [{ name: "Order Again", type: "light" }]
          }
          imagePosition="w-2/4 h-40"
        />
      ))}
    </div>
  ) : (
    <p className={design.text}>You don't have any orders yet</p>
  );
}

function Settings(props) {
  const position = {
    container: "grid grid-cols-1 md:grid-cols-2 gap-10",
    contentWrapper: "py-2",
    sidebar: "flex flex-col gap-5 *:flex *:gap-5",
    icon: "*:w-6 *:h-6",
    wrapper: "*:my-2",
  };
  const design = {
    sidebar: "text-gray-700",
    heading: "text-lg",
    menu: "cursor-pointer",
  };
  const [showText, setShowText] = useState(null);
  const menuItems = [
    { label: "Account Details", icon: <UserIcon /> },
    { label: "Delivery Addresses", icon: <HouseIcon /> },
    { label: "Shop Preferences", icon: <FilterIcon /> },
    { label: "Communication Preferences", icon: <ChatIcon /> },
    { label: "Privacy", icon: <PrivacyIcon /> },
    { label: "Profile Visibility", icon: <ShareIcon /> },
    { label: "Linked Accounts", icon: <LinkIcon /> },
  ];

  function onClick(e) {
    setShowText(e.target.innerHTML);
  }

  return (
    <div className={position.container}>
      <div className={`${position.sidebar} ${design.sidebar}`}>
        {menuItems.map((item) => (
          <div key={item.label} className={design.menu}>
            <div className={position.icon}>{item.icon}</div>
            <p onClick={onClick}>{item.label}</p>
          </div>
        ))}
      </div>
      {showText && (
        <div className={position.wrapper}>
          <h3 className={design.heading}>{showText}</h3>
          <div className={position.contentWrapper}>
            {showText === "Account Details" ? (
              <AccountDetails user={props.user} />
            ) : showText === "Delivery Addresses" ? (
              <DeliveryAddresses user={props.user} />
            ) : null}
          </div>
        </div>
      )}
    </div>
  );
}

function AccountDetails(props) {
  const position = { button: "my-3" };
  const navigate = useNavigate();
  const [data, setData] = useState({
    _id: props.user._id,
    email: props.user.email,
    password: props.user.password,
    phone: props.user.phone && props.user.phone.toString(),
    street: props.user.address.street,
    building: props.user.address.building,
    city: props.user.address.city,
    state: props.user.address.state,
    country: props.user.address.country,
    pincode: props.user.address.pincode,
  });
  const [err, setErr] = useState({
    email: "",
    password: "",
    phone: "",
    street: "",
    building: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
  });

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.email &&
      !errr.password &&
      !errr.phone &&
      !errr.street &&
      !errr.building &&
      !errr.city &&
      !errr.state &&
      !errr.country &&
      !errr.pincode
    ) {
      const user = {
        _id: props.user._id,
        address: {
          street: data.street,
          building: data.building,
          city: data.city,
          state: data.state,
          country: data.country,
          pincode: data.pincode,
        },
        email: data.email,
        fname: props.user.fname,
        lname: props.user.lname,
        password: data.password,
        phone: data.phone,
      };

      updateUser(user, (res) => {
        if (res) {
          setData(res);
          sessionStorage.setItem("user", JSON.stringify(res));
          navigate(0);
        }
      });
    }
  }

  return (
    <div>
      <Input
        name="email"
        placeholder="Email"
        value={data.email}
        onChange={onChange}
        errName={err.email}
      />
      <Input
        name="password"
        placeholder="Password"
        type="password"
        value={data.password}
        onChange={onChange}
        errName={err.password}
      />
      <Input
        name="phone"
        placeholder="Phone"
        value={data.phone}
        onChange={onChange}
        errName={err.phone}
      />
      <Input
        name="street"
        placeholder="Street"
        value={data.street}
        onChange={onChange}
        errName={err.street}
      />
      <Input
        name="building"
        placeholder="Building"
        value={data.building}
        onChange={onChange}
        errName={err.building}
      />
      <Input
        name="city"
        placeholder="City"
        value={data.city}
        onChange={onChange}
        errName={err.city}
      />
      <Input
        name="state"
        placeholder="State"
        value={data.state}
        onChange={onChange}
        errName={err.state}
      />
      <Input
        name="country"
        placeholder="Country"
        value={data.country}
        onChange={onChange}
        errName={err.country}
      />
      <Input
        name="pincode"
        placeholder="Pincode"
        value={data.pincode}
        onChange={onChange}
        errName={err.pincode}
      />
      <div className={position.button}>
        <Button text="Save" onClick={onClick} />
      </div>
    </div>
  );
}

function DeliveryAddresses(props) {
  const position = {
    wrapper: "my-3",
    formContainer: "fixed inset-0 flex items-center justify-center ",
    formWrapper: "p-6 w-11/12 max-w-md mx-auto",
    button: "mt-6",
    formbutton: "mt-3",
  };
  const design = {
    formContainer: "bg-black bg-opacity-50",
    formWrapper: "bg-white rounded-md",
    text: "font-light",
  };
  const [data, setData] = useState({
    fname: "",
    lname: "",
    phone: "",
    street: "",
    building: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
  });
  const [err, setErr] = useState({
    fname: "",
    lname: "",
    phone: "",
    street: "",
    building: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
  });
  const [address, setAddress] = useState({});
  const [boolean, setBoolean] = useState(false);
  const navigate = useNavigate();
  const ref = useRef();

  useEffect(() => {
    if (Object.values(data).every((value) => value === "") && props.user) {
      getAddress({ user: props.user._id }, setAddress);
    }
  }, [props.user, data]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (ref.current && !ref.current.contains(event.target)) {
        setBoolean(false);
      }
    };

    if (boolean) {
      document.addEventListener("mousedown", handleClickOutside);
      return () =>
        document.removeEventListener("mousedown", handleClickOutside);
    }
  }, [boolean]);

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }
  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.fname &&
      !errr.lname &&
      !errr.phone &&
      !errr.street &&
      !errr.building &&
      !errr.city &&
      !errr.state &&
      !errr.country &&
      !errr.pincode
    ) {
      const address = {
        user: props.user._id,
        fname: data.fname,
        lname: data.lname,
        phone: data.phone,
        address: {
          street: data.street,
          building: data.building,
          city: data.city,
          state: data.state,
          country: data.country,
          pincode: data.pincode,
        },
      };
      createAddress(address);
      navigate(0);
    }
  }

  return (
    <>
      {Object.keys(address).length > 0 && (
        <div className={position.wrapper}>
          <p className={design.text}>
            {address.fname} {address.lname}
          </p>
          <p className={design.text}>{address.address.street}</p>
          <p className={design.text}>{address.address.building}</p>
          <p className={design.text}>
            {address.address.city}, {address.address.state}{" "}
            {address.address.pincode}
          </p>
          <div className={position.button}>
            <Button text="Add Address" onClick={() => setBoolean(!boolean)} />
          </div>
        </div>
      )}
      {boolean && (
        <div className={`${position.formContainer} ${design.formContainer}`}>
          <div
            ref={ref}
            className={`${position.formWrapper} ${design.formWrapper}`}
          >
            <Input
              name="fname"
              placeholder="First Name"
              value={data.fname}
              onChange={onChange}
              errName={err.fname}
            />
            <Input
              name="lname"
              placeholder="Last Name"
              value={data.lname}
              onChange={onChange}
              errName={err.lname}
            />
            <Input
              name="street"
              placeholder="Street"
              value={data.street}
              onChange={onChange}
              errName={err.street}
            />
            <Input
              name="building"
              placeholder="Building"
              value={data.building}
              onChange={onChange}
              errName={err.building}
            />
            <Input
              name="city"
              placeholder="City"
              value={data.city}
              onChange={onChange}
              errName={err.city}
            />
            <Input
              name="state"
              placeholder="State"
              value={data.state}
              onChange={onChange}
              errName={err.state}
            />
            <Input
              name="country"
              placeholder="Country"
              value={data.country}
              onChange={onChange}
              errName={err.country}
            />
            <Input
              name="pincode"
              placeholder="Pincode"
              value={data.pincode}
              onChange={onChange}
              errName={err.pincode}
            />
            <Input
              name="phone"
              placeholder="Phone"
              value={data.phone}
              onChange={onChange}
              errName={err.phone}
            />
            <div className={position.formbutton}>
              <Button text="Save" onClick={onClick} />
            </div>
          </div>
        </div>
      )}
    </>
  );
}
