import axios from "axios";
import { Checkmark } from "react-checkmark";
import Layout from "../layout";

function Success() {
  const cart = JSON.parse(sessionStorage.getItem("cart")) || [];
  const checkout = JSON.parse(sessionStorage.getItem("checkout")) || [];

  useEffect(() => {
    const data = {};
    axios
      .post("/order", data)
      .then((res) => console.log(res.data))
      .catch((err) => console.log(err));
    sessionStorage.removeItem("cart");
    sessionStorage.removeItem("checkout");
  }, []);

  return (
    <Layout>
      <div className="my-20">
        <Checkmark size="10rem" />
        <h3 className="my-10 font-medium text-2xl text-center">
          Order placed successfully!
        </h3>
        <div className="grid grid-cols-2 gap-4 w-3/4 mx-auto">
          <div className="text-center">
            <h4 className="font-medium text-lg">Your Information</h4>
            <p className="capitalize">
              {checkout.delivery.first_name} {checkout.delivery.last_name}
            </p>
            <p className="lowercase">{checkout.delivery.email}</p>
            <p>
              {checkout.delivery.phone.replace(
                /(\d{3})(\d{3})(\d{4})/,
                "$1-$2-$3",
              )}
            </p>
          </div>
          <div className="text-center">
            <h4 className="font-medium text-lg">Payment Information</h4>
            {checkout.payment.mode === "Cash" ? (
              <div>
                <p>Cash on Delivery</p>
              </div>
            ) : (
              <div>
                <p>Card Payment</p>
                <p>XXXX-XXXX-XXXX-{checkout.payment.card_number.slice(-2)}</p>
                <p>
                  {checkout.payment.expiry_month +
                    " / " +
                    checkout.payment.expiry_year}
                </p>
              </div>
            )}
          </div>
          <div className="text-center">
            <h4 className="font-medium text-lg">Delivery Information</h4>
            <p>{checkout.delivery.address}</p>
          </div>
          <div className="text-center">
            <h4 className="font-medium text-lg">Shipping Information</h4>
            <p>{checkout.shipping}</p>
          </div>
        </div>
        <div className="flex flex-wrap gap-5 justify-center items-center my-10">
          {cart.map((item) => (
            <div
              key={item.product._id}
              className="w-36 h-36 border border-gray-200 rounded-md bg-gray-100"
            >
              <a href={"/product/" + item.product.slug}>
                <img
                  className="h-full p-2 object-contain"
                  src={item.product.image}
                  alt={item.product.name}
                />
              </a>
              <p className="font-medium text-center pt-2">
                {item.product.name}
              </p>
            </div>
          ))}
        </div>
      </div>
    </Layout>
  );
}

export default Success;
