import { useState, useEffect } from "react";
import Layout from "../layout";

function Cart() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const cart = JSON.parse(sessionStorage.getItem("cart"));
    if (cart) {
      const productItems = cart.map((item) => item.product);
      setProducts(productItems);
    }
  }, []);

  return (
    <Layout>
      <div className="w-9/12 pt-10 m-auto *:inline-block *:align-top">
        <div className="w-3/5">
          <h3 className="font-medium text-lg text-gray-700 mb-5">Bag</h3>
            {products && products.map((product) =>
              <>
                <div key={product._id} className="*:inline-block *:align-top">
                  <img
                    className="w-1/4 h-24 object-contain p-4 bg-gray-100 mr-4"
                    src={product.image}
                    alt={product.name}
                  />
                  <div className="w-4/12">
                    <h4>{product.name}</h4>
                    <h5>{product.category} Shoes</h5>
                    <h5>Size: </h5>
                  </div>
                  <h4 className="w-4/12 text-right mr-4">${product.price}</h4>
                </div>
                <hr className="h-px my-8 bg-gray-200 w-11/12 m-auto" />
              </>
            )}
        </div>
        <div className="w-1/3">
          <h3 className="font-medium text-lg text-gray-700 mb-5">Summary</h3>
        </div>
      </div>
    </Layout>
  );
}

export default Cart;
