function Input(props) {
  const position = "block w-full";
  const design = "border-gray-300 rounded-md hover:border-gray-700";

  return (
    <>
      <input
        className={`${position} ${design}`}
        name={props.name}
        value={props.value}
        placeholder={props.placeholder}
        onChange={props.onChange}
      />
      {item.errName && <p className={design.text}>{item.errName}</p>}
    </>
  );
}

export default Input;
