import { useEffect, useState } from "react";
import Layout from "../components/layout/layout";
import Input from "../components/input";

function Checkout() {
  const [data, setData] = useState({
    name: "",
  });
  const position = {
    container: "w-2/4 m-auto",
    heading: "pb-8",
    separator: "h-px my-4",
  };
  const design = {
    separator: "bg-gray-200",
    heading: "text-xl text-gray-900 font-medium",
  };

  useEffect(() => {
    console.log(data);
  }, [data]);

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  return (
    <Layout>
      <div className={position.container}>
        {[
          {
            name: "Delivery",
            component: <Delivery data={data} onChange={onChange} />,
          },
          {
            name: "Shipping",
            component: <Shipping data={data} onChange={onChange} />,
          },
          {
            name: "Payment",
            component: <Payment data={data} onChange={onChange} />,
          },
        ].map((item) => (
          <div key={item.name}>
            <h3 className={`${position.heading} ${design.heading}`}>
              {item.name}
            </h3>
            {item.component}
            <hr className={`${position.separator} ${design.separator}`} />
          </div>
        ))}
      </div>
    </Layout>
  );
}

export default Checkout;

function Delivery(props) {
  const items = [
    {
      name: "name",
      value: props.data.name,
      placeholder: "First Name",
      onChange: props.onChange,
      errName: "Please enter your name",
    },
  ];

  return (
    <div>
      <h1>Delivery</h1>
      <Item heading="What's your name and address" items={items} />
    </div>
  );
}

function Shipping() {
  return (
    <div>
      <h1>Shipping</h1>
    </div>
  );
}

function Payment() {
  return (
    <div>
      <h1>Payment</h1>
    </div>
  );
}

function Item(props) {
  const design = { heading: "font-medium", text: "text-sm text-red-500" };

  return (
    <>
      <h4 className={design.heading}>{props.heading}</h4>
      {props.items.map((item) => (
        <div key={item.name}>
          <Input
            key={item.name}
            name={item.name}
            value={item.value}
            onChange={item.onChange}
            placeholder={item.placeholder}
          />
          {item.errName && <p className={design.text}>{item.errName}</p>}
        </div>
      ))}
    </>
  );
}
