function Card(props) {
  const position = {
    container: "flex",
    wrapper: "h-96 w-64 border border-gray-400",
    image: "h-64",
  };
  const design = {
    image: "bg-gray-100 object-contain border border-transparent rounded-lg",
  };

  return (
    <div className={position.container}>
      {props.product && (
        <div className={position.wrapper}>
          <img
            className={`${position.image} ${design.image}`}
            src={props.product.image}
            alt={props.product.name}
          />
          <h3>{props.product.name}</h3>
          <h3>{props.product.category} Shoes</h3>
          <h3>
            <span>
              $
              {props.product.price -
                (props.product.price * props.product.discount) / 100}
            </span>
            <span>${props.product.price}</span>
          </h3>
        </div>
      )}
    </div>
  );
}

export default Card;
