import { useState } from "react";
import Layout from "../components/layout/layout";
import Input from "../components/input";
import Button from "../components/button";

function Checkout() {
  const position = {
    container: "w-2/4 m-auto",
    button: "*:w-full *:py-4 mt-5",
    separator: "mt-10 mb-4",
  };
  const design = { separator: "bg-gray-200" };
  const [data, setData] = useState({
    fname: "",
    lname: "",
    address: "",
    email: "",
    phone: "",
    date: "",
    payment: "",
    cname: "",
    cnumber: "",
    promo: "",
  });
  const [err, setErr] = useState({
    fname: "",
    lname: "",
    address: "",
    email: "",
    phone: "",
    cname: "",
    cnumber: "",
  });

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  function validate(name, value) {
    let error = false;
    let count = 0;
    let errMessage = "";
    switch (name) {
      case "fname":
        error = value.trim() === "";
        errMessage = "Please enter valid first name";
        break;
      case "lname":
        error = value.trim() === "";
        errMessage = "Please enter valid last name";
        break;
      case "address":
        error = value.trim() === "";
        errMessage = "Please enter valid address";
        break;
      case "email":
        error = !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value.trim());
        errMessage = "Please enter valid email address";
        break;
      case "phone":
        error = !/^\d{10}$/.test(value.trim());
        errMessage = "Please enter valid phone number";
        break;
      case "promo":
        error = !/^\d{5}$/.test(value.trim());
        errMessage = "Please enter valid promo code";
        break;
      case "cname":
        error = value.trim() === "";
        errMessage = "Please enter valid card name";
        break;
      case "cnumber":
        error = !/^\d{16}$/.test(value.trim());
        errMessage = "Please enter valid card number";
        break;
      default:
        break;
    }
    setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    return error ? count++ : count--;
  }

  function onClick() {
    let count = 0;
    Object.entries(data).forEach(([name, value]) => {
      count = validate(name, value);
    });
  }

  function date(days) {
    const today = new Date();
    const futureDate = new Date(today);
    futureDate.setDate(today.getDate() + days);
    const options = { weekday: "short", day: "numeric", month: "short" };
    return futureDate.toLocaleDateString("en-US", options);
  }

  return (
    <Layout>
      <div className={position.container}>
        <>
          <Title text="Delivery" />
          <Heading text="What's your name and address?" />
          <Input
            name="fname"
            value={data.fname}
            onChange={onChange}
            placeholder="First Name"
            errName={err.fname}
          />
          <Input
            name="lname"
            value={data.lname}
            onChange={onChange}
            placeholder="Last Name"
            errName={err.lname}
          />
          <Input
            name="address"
            value={data.address}
            onChange={onChange}
            placeholder="Address"
            errName={err.address}
          />
          <Heading text="What's your contact information?" />
          <Input
            name="email"
            value={data.email}
            onChange={onChange}
            placeholder="Email"
            errName={err.email}
          />
          <Input
            name="phone"
            value={data.phone}
            onChange={onChange}
            placeholder="Phone Number"
            errName={err.phone}
          />
          <div className={position.button}>
            <Button text="Continue" onClick={onClick} />
          </div>
          <hr className={`${position.separator} ${design.separator}`} />
        </>
        <>
          <Title text="Shipping" />
          <Heading text="When do you want the order?" />
          <Box
            text={`${date(4)} - ${date(7)}`}
            subtext="Free"
            boolean={data.date === `${date(4)} - ${date(7)}`}
            onClick={() =>
              setData((prev) => ({ ...prev, date: `${date(4)} - ${date(7)}` }))
            }
          />
          <Box
            text={`${date(7)} - ${date(10)}`}
            subtext="$50"
            boolean={data.date === `${date(7)} - ${date(10)}`}
            onClick={() =>
              setData((prev) => ({ ...prev, date: `${date(7)} - ${date(10)}` }))
            }
          />
          <div className={position.button}>
            <Button text="Continue" onClick={onClick} />
          </div>
          <hr className={`${position.separator} ${design.separator}`} />
        </>
        <>
          <Title text="Payment" />
          <Heading text="Have a promo code?" />
          <Input
            name="promo"
            value={data.promo}
            onChange={onChange}
            placeholder="Promo"
            errName={err.promo}
          />
          <Heading text="How would you like to pay?" />
          <Box
            text="Credit or Debit Card"
            boolean={data.payment === "Credit or Debit Card"}
            onClick={() =>
              setData((prev) => ({ ...prev, payment: "Credit or Debit Card" }))
            }
          />
          <Box
            text="Cash on Delivery"
            boolean={data.payment === "Cash on Delivery"}
            onClick={() =>
              setData((prev) => ({ ...prev, payment: "Cash on Delivery" }))
            }
          />
          {data.payment === "Credit or Debit Card" && (
            <>
              <Heading text="Enter your payment details:" />
              <Input
                name="cname"
                value={data.cname}
                onChange={onChange}
                placeholder="Name on card"
                errName={err.cname}
              />
              <Input
                name="cnumber"
                value={data.cnumber}
                onChange={onChange}
                placeholder="Card number"
                errName={err.cnumber}
              />
            </>
          )}
          <div className={position.button}>
            <Button text="Place Order" onClick={onClick} />
          </div>
          <hr className={`${position.separator} ${design.separator}`} />
        </>
      </div>
    </Layout>
  );
}

export default Checkout;

function Title(props) {
  const design = "font-medium text-xl text-gray-900";
  return <h3 className={design}>{props.text}</h3>;
}

function Heading(props) {
  const position = "mt-8";
  const design = "font-medium";
  return <h4 className={`${position} ${design}`}>{props.text}</h4>;
}

function Box(props) {
  const position = {
    container: "flex justify-between p-5 my-2 cursor-pointer",
  };
  const design = {
    container: `${
      props.boolean && "border-gray-700"
    } border border-2 rounded-xl hover:border-gray-700`,
    subtext: "font-light text-gray-500",
  };

  return (
    <div
      className={`${position.container} ${design.container}`}
      onClick={props.onClick}
    >
      <p>{props.text}</p>
      {props.subtext && <p className={design.subtext}>{props.subtext}</p>}
    </div>
  );
}
