import React, { useState } from "react";
import Tick from "../icons/tick";

function Filter() {
  const [filters, setFilters] = useState({
    price: 0,
    offer: 0,
    color: "",
    size: 0,
  });

  const handleRadioChange = (key, value) => {
    setFilters((prevFilters) => ({ ...prevFilters, [key]: value }));
    sendRequest();
  };

  const handleColorChange = (color) => {
    setFilters((prevFilters) => ({ ...prevFilters, color }));
    sendRequest();
  };

  const handleSizeChange = (size) => {
    setFilters((prevFilters) => ({ ...prevFilters, size }));
    sendRequest();
  };

  const sendRequest = () => {
    console.log("Sending request...");
  };

  return (
    <>
      <hr className="h-px my-4 bg-gray-200" />
      {[
        {
          title: "Price",
          options: ["<1000", "1000-2000", "2000-3000", ">4000"],
        },
        { title: "Offers", options: ["10", "20", "30"] },
      ].map(({ title, options }, index) => (
        <div key={index}>
          <h4 className="font-medium text-lg text-gray-700 mb-4">{title}</h4>
          {options.map((option, i) => (
            <div key={i} className="block">
              <input
                type="radio"
                className="w-4 h-4 my-1.5"
                value={option}
                checked={filters[title.toLowerCase()] === option}
                onChange={() => handleRadioChange(title.toLowerCase(), option)}
                onClick={() => sendRequest()}
              />
              <span className="text-sm ml-2">
                {title === "Price" ? option : `${option}% above`}
              </span>
            </div>
          ))}
          <hr className="h-px my-4 bg-gray-200" />
        </div>
      ))}
      <h4 className="font-medium text-lg text-gray-700">Color</h4>
      <div className="w-9/12 mt-2">
        {[
          "blue",
          "gray",
          "green",
          "indigo",
          "pink",
          "purple",
          "red",
          "yellow",
        ].map((color, i) => (
          <div
            key={i}
            className="inline-block w-5/12 text-center mt-5 mr-2 hover:brightness-125 hover:cursor-pointer"
            onClick={() => handleColorChange(color)}
          >
            {filters.color === color ? (
              <Tick />
            ) : (
              <span
                className={`w-5 h-5 bg-${color}-400 rounded-xl block m-auto`}
              />
            )}
            <span className="font-medium text-sm">{color}</span>
          </div>
        ))}
      </div>
      <hr className="h-px my-4 bg-gray-200" />
      <h4 className="font-medium text-lg text-gray-700 mb-4">Size</h4>
      <div className="flex flex-wrap">
        {[5, 5.5, 6, 6.5, 7, 7.5, 8, 8.5, 9, 9.5, 10, 10.5, 11, 11.5, 12].map(
          (size, i) => (
            <div
              key={i}
              className={`w-2/6 inline-block hover:border-gray-700 text-sm text-center border border-gray-200 rounded-md py-1.5 m-1 cursor-pointer ${
                filters.size === size ? "border border-gray-700" : ""
              }`}
              onClick={() => handleSizeChange(size)}
            >
              {size}
            </div>
          ),
        )}
      </div>
    </>
  );
}

export default Filter;
