import { useState } from "react";
import Layout from "../layout";

function Checkout() {
  const [activeIndex, setActiveIndex] = useState(0);

  return (
    <Layout>
      <div className="w-2/4 m-auto pt-10">
        {["Delivery", "Shipping", "Billing", "Payment"].map((item, index) => (
          <div key={index} onClick={() => setActiveIndex(index)}>
            <hr className="h-px my-4 bg-gray-200" />
            <h3
              className={`text-xl font-medium mb-8 ${
                activeIndex === index ? "text-gray-900" : "text-gray-500"
              }`}
            >
              {item}
            </h3>
            {item === "Delivery" ? (
              <Delivery />
            ) : item === "Shipping" ? (
              <Shipping />
            ) : item === "Billing" ? (
              <Billing />
            ) : (
              <Payment />
            )}
          </div>
        ))}
      </div>
    </Layout>
  );
}

function Delivery() {
  const [boolean, setBoolean] = useState(true);
  const [delivery, setDelivery] = useState({
    first_name: "",
    last_name: "",
    address: "",
    email: "",
    phone: "",
  });

  function onchange(e) {
    const { name, value } = e.target;
    setDelivery((prev) => ({ ...prev, [name]: value }));
  }

  function onsubmit() {
    let errors = [
      { first_name: "", last_name: "", address: "", email: "", phone: "" },
    ];
    Object.keys(delivery).forEach((key) => {
      const value = delivery[key];

      if (!value.trim()) {
        const newKey = key.includes("_") ? key.split("_").join(" ") : key;
        errors[key] = `Please enter your ${newKey}`;
      } else if (key === "email" && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) {
        errors[key] = "Please enter a valid email";
      } else if (key === "phone" && !/^\d{10}$/.test(value)) {
        errors[key] = "Please enter a valid phone number";
      }
    });
    console.log(errors);
  }

  return (
    <>
      {boolean && (
        <div>
          <h4 className="font-medium">Enter your name and address:</h4>
          <div className="*:block *:w-full *:border *:border-gray-300 *:rounded-md *:p-4 *:mt-4">
            <input
              name="first_name"
              value={delivery.first_name}
              onChange={(e) => onchange(e)}
              className="hover:border-gray-700"
              placeholder="First Name"
            />
            <input
              name="last_name"
              value={delivery.last_name}
              onChange={(e) => onchange(e)}
              className="hover:border-gray-700"
              placeholder="Last Name"
            />
            <input
              name="address"
              value={delivery.address}
              onChange={(e) => onchange(e)}
              className="hover:border-gray-700"
              placeholder="Address"
            />
          </div>
          <h4 className="font-medium mt-10">
            What's your contact information?
          </h4>
          <div className="*:block *:w-full *:border *:border-gray-300 *:rounded-md *:p-4 *:mt-4">
            <input
              name="email"
              value={delivery.email}
              onChange={(e) => onchange(e)}
              className="hover:border-gray-700"
              placeholder="Email"
            />
            <input
              name="phone"
              value={delivery.phone}
              onChange={(e) => onchange(e)}
              className="hover:border-gray-700"
              placeholder="Phone Number"
            />
          </div>
          <div className="flex flex-col gap-2 mt-8 *:border *:border-gray-700 *:rounded-3xl *:py-3 *:font-medium">
            <button
              onClick={() => onsubmit()}
              className="bg-gray-800 text-white"
            >
              Continue
            </button>
          </div>
        </div>
      )}
    </>
  );
}

function Shipping() {
  return (
    <div>
      <h4>Shipping content goes here</h4>
    </div>
  );
}

function Billing() {
  return (
    <div>
      <h4>Billing content goes here</h4>
    </div>
  );
}

function Payment() {
  return (
    <div>
      <h4>Payment content goes here</h4>
    </div>
  );
}

export default Checkout;
