import { useState } from "react";
import Input from "../components/input";
import Button from "../components/button";
import Logo from "../icons/logo";
import { validate } from "../helper";

function Register() {
  const [email, setEmail] = useState("");
  const [err, setErr] = useState("");
  const position = {
    container: "flex flex-col gap-5 w-4/6 md:w-3/6 lg:w-3/6 m-auto py-20",
    heading: "mb-3",
  };
  const design = {
    heading: "text-3xl text-gray-700",
  };

  function onClick() {
    const { error, errMessage } = validate("email", email);
    setErr(error ? errMessage : "");
    console.log(!error && "TETT");
  }

  return (
    <div className={position.container}>
      <Logo />
      <h1 className={`${position.heading} ${design.heading}`}>
        Enter your email to join or sign in.
      </h1>
      <div>
        <Input
          name="email"
          value={email}
          placeholder="Email"
          onChange={(e) => setEmail(e.target.value)}
          errName={err}
        />
      </div>
      <div className={position.button}>
        <Button text="Continue" onClick={onClick} />
      </div>
    </div>
  );
}

export default Register;
