import React, { useState } from "react";
import axios from "axios";
import Tick from "../icons/tick";

const Radio = ({ label, value, selectedValue, handleChange }) => (
  <div className="block">
    <input
      type="radio"
      className="w-4 h-4 my-1.5"
      value={value}
      checked={selectedValue === value}
      onChange={() => handleChange(value)}
    />
    <span className="text-sm ml-2">{label}</span>
  </div>
);

const ColorOption = ({ color, selectedValue, handleClick }) => (
  <div
    className={`inline-block w-5/12 text-center mt-5 mr-2 hover:brightness-125 hover:cursor-pointer`}
    onClick={() => handleClick(color)}
  >
    {selectedValue === color ? (
      <Tick />
    ) : (
      <span className={`w-5 h-5 bg-${color}-400 rounded-xl block m-auto`} />
    )}
    <span className="font-medium text-sm">{color}</span>
  </div>
);

const SizeOption = ({ size, selectedValue, handleClick }) => (
  <div
    className={`w-2/6 inline-block hover:border-gray-700 text-sm text-center border border-gray-200 rounded-md py-1.5 m-1 cursor-pointer ${
      selectedValue === size ? "border border-gray-700" : ""
    }`}
    onClick={() => handleClick(size)}
  >
    {size}
  </div>
);

function Filter() {
  const priceRanges = [
    { label: "Below $1000", value: "<1000" },
    { label: "$1000 - $2000", value: "1000-2000" },
    { label: "$2000 - $3000", value: "2000-3000" },
    { label: "Above $4000", value: ">4000" },
  ];
  const discountOffers = ["10", "20", "30"];
  const colors = [
    "blue",
    "gray",
    "green",
    "indigo",
    "pink",
    "purple",
    "red",
    "yellow",
  ];
  const sizes = [
    "5",
    "5.5",
    "6",
    "6.5",
    "7",
    "7.5",
    "8",
    "8.5",
    "9",
    "9.5",
    "10",
    "10.5",
    "11",
    "11.5",
    "12",
  ];

  const [filters, setFilters] = useState({
    price: 0,
    offer: 0,
    color: "",
    size: "",
  });

  const changeFilters = (key, value) => {
    setFilters((prev) => ({
      ...prev,
      [key]: value,
    }));
    axios
      .post("/products", filters)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

  return (
    <>
      <hr className="h-px my-4 bg-gray-200" />
      <h4 className="font-medium text-lg text-gray-700 mb-4">Price</h4>
      {priceRanges.map(({ label, value }, index) => (
        <Radio
          key={index}
          label={label}
          value={value}
          selectedValue={filters.price}
          handleChange={(value) => changeFilters("price", value)}
        />
      ))}
      <hr className="h-px my-4 bg-gray-200" />
      <h4 className="font-medium text-lg text-gray-700 mb-4">Offers</h4>
      {discountOffers.map((discount, index) => (
        <Radio
          key={index}
          label={`${discount}% above`}
          value={discount}
          selectedValue={filters.offer}
          handleChange={(value) => changeFilters("offer", value)}
        />
      ))}
      <hr className="h-px my-4 bg-gray-200" />
      <h4 className="font-medium text-lg text-gray-700">Color</h4>
      <div className="w-9/12 mt-2">
        {colors.map((color, index) => (
          <ColorOption
            key={index}
            color={color}
            selectedValue={filters.color}
            handleClick={(color) => changeFilters("color", color)}
          />
        ))}
      </div>
      <hr className="h-px my-4 bg-gray-200" />
      <h4 className="font-medium text-lg text-gray-700 mb-4">Size</h4>
      <div className="flex flex-wrap">
        {sizes.map((size, index) => (
          <SizeOption
            key={index}
            size={size}
            selectedValue={filters.size}
            handleClick={(size) => changeFilters("size", size)}
          />
        ))}
      </div>
    </>
  );
}

export default Filter;
