import { useEffect, useState } from "react";
import { getProducts } from "../server";
import Layout from "../components/layout/layout";
import Grid from "../components/grid";
import Topbar from "../components/collection/topbar";

function CollectionX(props) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    getProducts({ category: props.category }, setProducts);
  }, [props.category]);

  function sort(e) {
    switch (e.target.textContent) {
      case "Newest":
        setProducts(
          [...products].sort(
            (a, b) => new Date(b.created) - new Date(a.created),
          ),
        );
        break;
      case "Price: High to Low":
        setProducts([...products].sort((a, b) => b.price - a.price));
        break;
      case "Price: Low to High":
        setProducts([...products].sort((a, b) => a.price - b.price));
        break;
      default:
        break;
    }
  }

  return (
    <Layout>
      <Topbar products={products} category={props.category} onClick={sort} />
      <Grid products={products} />
    </Layout>
  );
}

export default CollectionX;
