import React, { useState, useEffect } from "react";
import { getStatesByCountry } from "world-countries-capitals";

function SelectState(props) {
  const [states, setStates] = useState([]);

  useEffect(() => {
    props.country
      ? setStates(getStatesByCountry(props.country))
      : setStates([]);
  }, [props.country]);

  return (
    <select disabled={!props.country}>
      {states.map((item) => (
        <option key={item.name} value={item.name}>
          {item.name}
        </option>
      ))}
    </select>
  );
}

export default SelectState;
