import DarrowIcon from "../icons/darrow";

function dropdown2(props) {
  const position = {
    container: "relative",
    mainWrapper: "flex gap-2 items-center",
    wrapper: "absolute right-0 z-10 w-max",
    item: "py-2 px-4",
  };
  const design = {
    container: "cursor-pointer",
    wrapper: "bg-white rounded-lg shadow-md",
    item: "rounded-lg hover:bg-gray-50",
  };
  return (
    <details
      className={`${position.container} ${design.container} ${props.className}`}
    >
      <summary className={position.mainWrapper}>
        <p>{props.heading}</p>
        <DarrowIcon />
      </summary>
      <div className={`${position.wrapper} ${design.wrapper}`}>
        {props.text.map((item, index) => (
          <p
            key={index}
            className={`${position.item} ${design.item}`}
            onClick={props.onClick}
          >
            {item}
          </p>
        ))}
      </div>
    </details>
  );
}

export default dropdown2;
