import { useState, useEffect } from "react";
import Layout from "../components/layout/layout";
import Bin from "../icons/bin";
import { getDiscountedPrice } from "../helper";

function Cart() {
  const [cart, setCart] = useState([]);
  const position = {
    container: "flex flex-col md:flex-row gap-16 w-9/12 m-auto",
    leftwrapper: "md:w-3/5",
    rightwrapper: "md:w-2/5",
    leftsubwrapper: "grid grid-cols-3 gap-4 my-5",
    leftsubsubwrapper: "flex flex-col justify-between",
    rightsubwrapper: "flex my-5",
  };
  const design = {
    heading: "font-medium text-xl text-gray-700",
    image: "bg-gray-100 object-contain",
  };

  useEffect(() => {
    setCart(JSON.parse(sessionStorage.getItem("cart")));
  }, []);

  return (
    <Layout>
      <div className={position.container}>
        <div className={position.leftwrapper}>
          <h2 className={design.heading}>Bag</h2>
          {cart.map((item) => (
            <div className={position.leftsubwrapper}>
              <img
                className={`${design.image}`}
                src={item.product.image}
                alt={item.product.name}
              />
              <div className={position.leftsubsubwrapper}>
                <div>
                  <p>{item.product.name}</p>
                  <p>{item.product.category} Shoes</p>
                </div>
                <div>
                  <p>Size: {item.size}</p>
                  <p>Color: {item.color}</p>
                </div>
              </div>
              <div className={position.leftsubsubwrapper}>
                <div>
                  <p>
                    $
                    {getDiscountedPrice(
                      item.product.price,
                      item.product.discount,
                    )}
                  </p>
                  <p>${item.product.price}</p>
                </div>
                <div>
                  <Bin />
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className={position.rightwrapper}>
          <h2 className={design.heading}>Summary</h2>
          <div className={position.rightsubwrapper}></div>
        </div>
      </div>
    </Layout>
  );
}

export default Cart;
