import { useEffect, useState } from "react";
import Layout from "../components/layout/layout";
import Card from "../components/card";
import { getProducts } from "../server";
import DarrowIcon from "../icons/darrow";
import FilterIcon from "../icons/filter";

function Collection(props) {
  const [products, setProducts] = useState([]);
  const position =
    "grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 2xl:grid-cols-6 gap-4 px-4";

  useEffect(() => {
    getProducts({ category: props.category }, setProducts);
  }, [props.category]);

  return (
    <Layout>
      <Topbar category={props.category} products={products} />
      <div className={position}>
        {products &&
          products.map((product) => (
            <Card key={product._id} product={product} />
          ))}
      </div>
    </Layout>
  );
}

function Topbar(props) {
  const position = {
    container: "flex justify-between px-5 py-5",
    wrapper: "flex gap-5",
  };
  const design = {
    heading: "font-medium text-2xl",
    text: "font-light tracking-wide",
  };

  return (
    <div className={position.container}>
      <h1 className={design.heading}>
        {props.category} Shoes ({props.products.length})
      </h1>
      <div className={position.wrapper}>
        <h2 className={design.text}>
          Show Filters <FilterIcon />
        </h2>
        <h2 className={design.text}>
          Sort By <DarrowIcon />
        </h2>
      </div>
    </div>
  );
}

export default Collection;
