import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Layout from "../components/layout/layout";
import Button from "../components/button";
import Bin from "../icons/bin";
import { postCart } from "../server";
import { getDiscountedPrice } from "../helper";

function Cart() {
  const navigate = useNavigate();
  const position = {
    container: "flex flex-col md:flex-row gap-16 w-9/12 m-auto",
  };

  function productData() {
    return cart.map((item) => ({
      id: item.product._id,
      size: item.size,
      color: item.color,
      image: item.product.image,
      name: item.product.name,
      category: item.product.category,
      price: item.product.price,
      discount: item.product.discount,
    }));
  }
  function trash(productId) {
    //const data = cart.filter((item) => item.product._id !== productId);
    //setCart(data);
    //sessionStorage.setItem("cart", JSON.stringify(data));
  }
  function checkout() {}

  return (
    <Layout>
      <div className={position.container}>
        <Product productData={productData} onClick={trash} />
        <Price onClick={checkout} />
      </div>
    </Layout>
  );
}

export default Cart;

function Product(props) {
  const position = {
    container: "md:w-3/5",
    text: "my-5",
  };
  const design = {
    heading: "font-medium text-xl text-gray-700",
    separator: "bg-gray-200",
  };

  return (
    <div className={position.container}>
      <h1 className={design.heading}>Bag</h1>
      {props.productData().length > 0 ? (
        props.productData().map((item) => (
          <div key={item.id}>
            <Card
              id={item.id}
              size={item.size}
              color={item.color}
              image={item.image}
              name={item.name}
              category={item.category}
              price={item.price}
              discount={item.discount}
              onClick={props.onClick}
              priceData={props.priceData}
            />
            <hr className={design.separator} />
          </div>
        ))
      ) : (
        <p className={position.text}>There are no items in your bag</p>
      )}
    </div>
  );
}

function Price(props) {
  const position = {
    buttonWrapper: "*:w-full *:py-3",
    container: "md:w-2/5",
    item: "flex justify-between my-3",
    separator: "h-px my-5",
    wrapper: "my-5",
  };
  const design = {
    buttonWrapper: "font-medium *:bg-gray-800",
    heading: "font-medium text-xl text-gray-700",
    item: "text-gray-700",
    separator: "bg-gray-200",
    text: "font-medium text-gray-900",
  };

  return (
    <div className={position.container}>
      <h2 className={design.heading}>Summary</h2>
      <div className={position.wrapper}>
        <div className={`${position.item} ${design.item}`}>
          <span>Subtotal</span>
          <span>${discountPrice}</span>
        </div>
        <div className={`${position.item} ${design.item}`}>
          <span>Tax</span>
          <span>${tax}</span>
        </div>
        <hr className={`${position.separator} ${design.separator}`} />
        <div className={`${position.item} ${design.item}`}>
          <span>Total</span>
          <span className={design.text}>${total}</span>
        </div>
        <hr className={`${position.separator} ${design.separator}`} />
      </div>
      <div className={`${position.buttonWrapper} ${design.buttonWrapper}`}>
        <Button text="Checkout" onClick={props.onClick} />
      </div>
    </div>
  );
}

function Card(props) {
  const position = {
    container: "flex my-5",
  };

  return (
    <div className={position.container}>
      <Image image={props.image} name={props.name} />
      <ProductInfo
        id={props.id}
        size={props.size}
        color={props.color}
        name={props.name}
        category={props.category}
        price={props.price}
        discount={props.discount}
        onClick={props.onClick}
      />
    </div>
  );
}

function Image(props) {
  const position = {
    item: "w-1/4 p-4",
  };
  const design = {
    item: "bg-gray-100 object-contain",
  };

  return (
    <img
      className={`${position.item} ${design.item}`}
      src={props.image}
      alt={props.name}
    />
  );
}

function ProductInfo(props) {
  const position = {
    container: "grid grid-cols-2 grid-rows-2 gap-4 mx-4 w-full",
    wrapper: "flex flex-col",
    rightwrapper: "items-end",
    bottomwrapper: "justify-end",
  };
  const design = {
    textstrike: "text-gray-300 line-through",
    text: "font-light text-gray-500",
    subtext: "font-light text-sm text-gray-500",
  };

  return (
    <div className={position.container}>
      <div className={position.wrapper}>
        <p>{props.name}</p>
        <p className={design.text}>{props.category} Shoes</p>
      </div>
      <div className={`${position.wrapper} ${position.rightwrapper}`}>
        <p>${getDiscountedPrice(props.price, props.discount)}</p>
        <p className={design.textstrike}>${props.price}</p>
      </div>
      <div className={`${position.wrapper} ${position.bottomwrapper}`}>
        <p className={design.subtext}>Size: {props.size}</p>
        <p className={design.subtext}>Color: {props.color}</p>
      </div>
      <div
        className={`${position.wrapper} ${position.rightwrapper} ${position.bottomwrapper}`}
      >
        <Bin onClick={() => props.onClick(props.id)} />
      </div>
    </div>
  );
}
