import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Input from "../components/input";
import Button from "../components/button";
import Logo from "../icons/logo";
import { validate } from "../helper";

function Register() {
  const [data, setData] = useState({
    email: "",
    fname: "",
    lname: "",
    password: "",
  });
  const [err, setErr] = useState({
    email: "",
    fname: "",
    lname: "",
    password: "",
  });
  const [show, setShow] = useState("email");
  const navigate = useNavigate();

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (!errr.email) {
      setShow("register");
    }
    if (!errr.password) {
      navigate("/login");
    }
  }

  return (
    <>
      {show === "email" && (
        <Form
          heading="Enter your email to join or sign in."
          name="email"
          placeholder="Email"
          data={data.email}
          err={err.email}
          onChange={onChange}
          onClick={onClick}
          buttonText="Continue"
        />
      )}
      {show === "register" && (
        <FormBig
          onClick={onClick}
          buttonText="Create Account"
          data={data}
          err={err}
          onChange={onChange}
        />
      )}
    </>
  );
}

export default Register;

function Form(props) {
  const position = {
    container: "flex flex-col gap-5 w-4/6 md:w-3/6 lg:w-3/6 m-auto py-20",
    heading: "mb-3",
    button: "*:w-full *:p-3",
  };
  const design = {
    heading: "text-3xl text-gray-700",
  };

  return (
    <div className={position.container}>
      <Logo />
      <h1 className={`${position.heading} ${design.heading}`}>
        {props.heading}
      </h1>
      <div>
        <Input
          name={props.name}
          value={props.data}
          placeholder={props.placeholder}
          onChange={props.onChange}
          errName={props.err}
          type={props.type}
        />
      </div>
      <div className={position.button}>
        <Button text={props.buttonText} onClick={props.onClick} />
      </div>
    </div>
  );
}

function FormBig(props) {
  const position = {
    container: "flex flex-col gap-5 w-4/6 md:w-3/6 lg:w-3/6 m-auto py-20",
    inputWrapper: "flex gap-4 justify-between",
    button: "*:w-full *:p-3",
  };
  const design = {};

  return (
    <div className={position.container}>
      <div className={position.inputWrapper}>
        <Input
          name="fname"
          value={props.data.fName}
          placeholder="First Name"
          onChange={props.onChange}
          errName={props.err.fname}
        />
        <Input
          name="lname"
          value={props.data.lname}
          placeholder="Last Name"
          onChange={props.onChange}
          errName={props.err.lname}
        />
      </div>
      <Input
        name="password"
        type="password"
        value={props.data.password}
        placeholder="Password"
        onChange={props.onChange}
        errName={props.err.password}
      />
      <div className={position.button}>
        <Button text={props.buttonText} onClick={props.onClick} />
      </div>
    </div>
  );
}
