import { useState } from "react";
import Layout from "../layout";
import Input from "../components/input";
import Button from "../components/button";

function Checkout() {
  const [activeIndex, setActiveIndex] = useState(0);

  return (
    <Layout>
      <div className="w-2/4 m-auto pt-10">
        {["Delivery", "Shipping", "Billing", "Payment"].map((item, index) => (
          <div key={index} onClick={() => setActiveIndex(index)}>
            <hr className="h-px my-4 bg-gray-200" />
            <h3
              className={`text-xl font-medium mb-8 ${
                activeIndex === index ? "text-gray-900" : "text-gray-500"
              }`}
            >
              {item}
            </h3>
            {item === "Delivery" ? (
              <Delivery />
            ) : item === "Shipping" ? (
              <Shipping />
            ) : item === "Billing" ? (
              <Billing />
            ) : (
              <Payment />
            )}
          </div>
        ))}
      </div>
    </Layout>
  );
}

function Delivery() {
  const [boolean, setBoolean] = useState(false);
  const [errors, setErrors] = useState({});
  const [delivery, setDelivery] = useState({
    first_name: "",
    last_name: "",
    address: "",
    email: "",
    phone: "",
  });

  function handleChange(e) {
    const { name, value } = e.target;
    setDelivery((prevState) => ({ ...prevState, [name]: value }));
  }

  function handleSubmit() {
    let newErrors = {};
    Object.keys(delivery).forEach((key) => {
      const value = delivery[key].trim();
      switch (key) {
        case "email":
          if (!value || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) {
            newErrors[key] = "Please enter a valid email";
          }
          break;
        case "phone":
          if (!value || !/^\d{10}$/.test(value)) {
            newErrors[key] = "Please enter a valid phone number";
          }
          break;
        default:
          if (!value) {
            newErrors[key] = `Please enter your ${key.replace("_", " ")}`;
          }
          break;
      }
    });
    setErrors(newErrors);
    Object.keys(newErrors).length === 0 && setBoolean(true);
    console.log(delivery);
  }
  return (
    <>
      {boolean === false && (
        <div>
          <h4 className="font-medium">What's your name and address?</h4>
          <Input
            name="first_name"
            value={delivery.first_name}
            onChange={handleChange}
            placeholder="First Name"
          />
          {errors.first_name && (
            <span className="block text-sm text-red-500">
              {errors.first_name}
            </span>
          )}{" "}
          <Input
            name="last_name"
            value={delivery.last_name}
            onChange={handleChange}
            placeholder="Last Name"
          />
          {errors.last_name && (
            <span className="block text-sm text-red-500">
              {errors.last_name}
            </span>
          )}
          <Input
            name="address"
            value={delivery.address}
            onChange={handleChange}
            placeholder="Address"
          />
          {errors.address && (
            <span className="block text-sm text-red-500">{errors.address}</span>
          )}
          <h4 className="font-medium mt-10">
            What's your contact information?
          </h4>
          <Input
            name="email"
            value={delivery.email}
            onChange={handleChange}
            placeholder="Email"
          />
          {errors.email && (
            <span className="block text-sm text-red-500">{errors.email}</span>
          )}
          <Input
            name="phone"
            value={delivery.phone}
            onChange={handleChange}
            placeholder="Phone Number"
          />
          {errors.phone && (
            <span className="block text-sm text-red-500 mb-5">
              {errors.phone}
            </span>
          )}
          <Button onClick={handleSubmit} text="Continue" />
        </div>
      )}
    </>
  );
}

function Shipping() {
  const [boolean, setBoolean] = useState(false);
  const [active, setActive] = useState(0);
  const [error, setError] = useState(false);
  const [shipping, setShipping] = useState("");
  const shippingCost = 40;

  function setDeliveryDate(i) {
    function days(daysFromToday) {
      const string = new Date(
        new Date().setDate(new Date().getDate() + daysFromToday),
      );
      const day = string.toLocaleDateString("en-US", { weekday: "short" });
      const date = string.toLocaleDateString("en-US", { day: "numeric" });
      const month = string.toLocaleDateString("en-US", { month: "short" });
      return `${day}, ${date} ${month}`;
    }
    switch (i) {
      case 1:
        setShipping(`Arrives ${days(2)} - ${days(4)}`);
        break;
      case 2:
        setShipping(`Arrives ${days(5)} - ${days(7)}`);
        break;
      default:
        setShipping("");
        break;
    }
    return shipping;
  }

  function handleSubmit() {
    console.log(shipping);
    shipping ? setBoolean(true) : setError(true);
  }

  return (
    <>
      {boolean === false && (
        <div>
          <h4 className="font-medium text-xl">When do you want the order?</h4>
          {[1, 2].map((i) => (
            <div
              key={i}
              onClick={() => {
                setActive(active === i ? null : i);
                setError(false);
              }}
              className={`flex justify-between border border-2 rounded-xl px-4 py-6 mt-5 cursor-pointer font-medium ${
                active === i && "border-gray-700"
              }`}
            >
              <p>{setDeliveryDate(i)}</p>
              <p className="text-gray-500">
                {i === 1 && "$" + shippingCost + ".00"}
              </p>
            </div>
          ))}
          <p className="text-sm text-red-500 mx-1 mt-2">
            {error && "Please select delivery option"}
          </p>
          <Button onClick={handleSubmit} text="Continue" />
        </div>
      )}
    </>
  );
}

function Billing() {
  return (
    <div>
      <h4>Billing content goes here</h4>
    </div>
  );
}

function Payment() {
  return (
    <div>
      <h4>Payment content goes here</h4>
    </div>
  );
}

export default Checkout;
