import { useState, useEffect } from "react";
import Layout from "../layout";

function Cart() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const cart = JSON.parse(sessionStorage.getItem("cart"));
    if (cart) {
      const productItems = cart.map((item) => item.product);
      setProducts(productItems);
    }
  }, []);

  return (
    <Layout>
      <div className="w-9/12 pt-10 m-auto *:inline-block *:align-top">
        <div className="w-3/5">
          <h3 className="font-medium text-xl text-gray-700 mb-5">Bag</h3>
            {products && products.map((product) =>
              <div key={product._id}>
                <div className="*:inline-block *:align-top">
                  <img
                    className="w-1/4 h-24 object-contain p-4 bg-gray-100 mr-4"
                    src={product.image}
                    alt={product.name}
                  />
                  <div className="w-4/12">
                    <h4>{product.name}</h4>
                    <h5 className="text-gray-500">{product.category} Shoes</h5>
                    <h5 className="text-gray-500">Size: </h5>
                  </div>
                  <h4 className="w-4/12 text-right mr-4">${product.price}</h4>
                </div>
                <hr className="h-px my-8 bg-gray-200 w-11/12 m-auto" />
              </div>
            )}
        </div>
        <div className="w-2/5 text-gray-700">
          <h3 className="font-medium text-xl text-gray-700 mb-5">Summary</h3>
          <div className="mb-3">
            <h5 className="inline-block w-9/12">Subtotal</h5>
            <h5 className="inline-block w-3/12 text-right">
              ${products.reduce((total, product) => total + parseFloat(product.price), 0)}
            </h5>
          </div>
          <div className="">
            <h5 className="inline-block w-9/12">Estimated delivery & handling</h5>
            <h5 className="inline-block w-3/12 text-right">$1250</h5>
          </div>
          <hr className="h-px my-5 bg-gray-200" />
          <div className="">
            <h5 className="inline-block w-9/12">Total</h5>
            <h5 className="inline-block w-3/12 text-right font-medium text-gray-900">
              ${products.reduce((total, product) => total + parseFloat(product.price), 0) +1250}
            </h5>
          </div>
          <hr className="h-px my-5 bg-gray-200" />
          <div className="flex flex-col gap-2 mt-8 *:border *:border-gray-700 *:rounded-3xl *:py-3 *:font-medium">
            <button className="bg-gray-800 text-white">Checkout</button>
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Cart;
