import { useState } from "react";
import LArrowIcon from "../icons/larrow";
import RArrowIcon from "../icons/rarrow";

function Slider({ products, heading, subheading }) {
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleScroll = (scrollOffset) => {
    const container = document.querySelector(".hide-scrollbar");
    if (container) {
      const newPosition = scrollPosition + scrollOffset;
      container.scrollTo({
        left: newPosition >= 0 ? newPosition : 0,
        behavior: "smooth",
      });
      if (
        newPosition >= 0 &&
        newPosition <= container.scrollWidth - container.clientWidth
      ) {
        setScrollPosition(newPosition);
      } else {
        setScrollPosition(0);
      }
    }
  };

  console.log(scrollPosition);

  return (
    <>
      <div className="flex items-center justify-between mt-5">
        <p className="text-2xl">{heading}</p>
        <div className="flex items-center space-x-3">
          <p>{subheading}</p>
          <button
            className="rounded-3xl p-2.5 bg-gray-200 hover:bg-gray-300"
            onClick={() => handleScroll(-500)}
          >
            <LArrowIcon />
          </button>
          <button
            className="rounded-3xl p-2.5 bg-gray-200 hover:bg-gray-300"
            onClick={() => handleScroll(500)}
          >
            <RArrowIcon />
          </button>
        </div>
      </div>
      <div className="flex gap-2 hide-scrollbar overflow-x-scroll my-5">
        {products &&
          products.map((product) => (
            <a
              key={product._id}
              className="flex-shrink-0"
              style={{ width: "30vw" }}
              href={`/product/${product.slug}`}
            >
              <div className="flex flex-col">
                <img
                  key={product._id}
                  className="bg-gray-100 px-5 object-contain w-full border border-transparent rounded-lg"
                  style={{ height: "45vh" }}
                  src={product.image}
                  alt={product.name}
                />
                <p className="font-medium mt-2 text-gray-900">{product.name}</p>
                <p className="text-gray-500">{product.category} Shoes</p>
                <p className="font-medium mt-2 text-gray-900">
                  ${product.price}.00
                </p>
              </div>
            </a>
          ))}
      </div>
    </>
  );
}

export default Slider;
