import { useState, useEffect } from "react";
import axios from "axios";
import Layout from "../layout";
import Slider from "../components/slider";

function Favorites() {
  const favorites = JSON.parse(sessionStorage.getItem("favorite")) || [];
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios
      .post("/products")
      .then((res) => setProducts(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <Layout>
      <div className="w-10/12 py-10 m-auto">
        <h3 className="font-medium text-2xl text-gray-700 mb-5">Favorites</h3>
        <div className="pt-28 pb-10">
          {favorites ? (
            favorites.map((product) => <div></div>)
          ) : (
            <p className="font-medium text-center">
              You can find items you've favorited saved here.
            </p>
          )}
        </div>
        <Slider products={products} heading="Discover Your Next Favorites" />
      </div>
    </Layout>
  );
}

export default Favorites;
