import { useEffect, useState } from "react";
import Layout from "../components/layout/layout";
import { createOrder } from "../server";
import { today } from "../helper";

function Success() {
  const position = {
    container: "flex flex-col gap-2 md:items-center my-5",
    wrapper: "py-10",
  };

  const [order, setOrder] = useState(
    JSON.parse(sessionStorage.getItem("order")) || {},
  );

  useEffect(() => {
    const cart = JSON.parse(sessionStorage.getItem("cart"));
    const user = JSON.parse(sessionStorage.getItem("user"));
    const shipping = JSON.parse(sessionStorage.getItem("shipping"));
    const payment = JSON.parse(sessionStorage.getItem("payment"));

    !order._id &&
      cart &&
      user &&
      shipping &&
      payment &&
      createOrder(
        {
          cart: cart.map((item) => item._id),
          user: user._id,
          shipping: shipping._id,
          payment: payment._id,
        },
        (data) => {
          if (data) {
            sessionStorage.setItem("order", JSON.stringify(data));
            setOrder(data);
          }
        },
      );
  }, [order]);

  return (
    <Layout>
      <div className={position.container}>
        <Heading text="Thanks! We're on it." />
        <Subtext text={`Ordered ${today()}`} />
        <Subtext text={`#` + order._id} />
        <Subtext text="Purchased Online" />
        <Separator />
        <div className={position.wrapper}>
          <Badge text="Ordered" />
        </div>
      </div>
    </Layout>
  );
}

export default Success;

function Subheading(props) {
  const position = "my-2";
  const design = "font-medium text-lg";

  return <h3 className={`${position} ${design}`}>{props.text}</h3>;
}

function Heading(props) {
  const position = "pt-10";
  const design = "font-medium text-3xl";

  return <h2 className={`${position} ${design}`}>{props.text}</h2>;
}

function Text(props) {
  const position = "my-2";
  const design = props.type === "lowercase" ? "lowercase" : "capitalize";

  return <p className={`${position} ${design}`}>{props.text}</p>;
}

function Subtext(props) {
  const design = `font-light text-xl text-gray-500 ${
    props.type === "lowercase" ? "lowercase" : "capitalize"
  }`;

  return <p className={design}>{props.text}</p>;
}

function Badge(props) {
  const design = { text: "text-xl font-medium text-green-700" };
  return <h3 className={design.text}>{props.text}</h3>;
}

function Separator() {
  const position = "h-2 md:w-3/4 md:mx-auto my-10";
  const design = "bg-gray-100";

  return <hr className={`${position} ${design}`} />;
}
