import Layout from "../components/layout/layout";
import Button from "../components/button";

function Cart() {
  const cart = JSON.parse(sessionStorage.getItem("cart")) || [];
  const position = {
    container: "flex w-9/12 m-auto",
  };

  function priceData() {
    const totalPrice = cart.reduce((acc, item) => acc + item.product.price, 0);
    const totalDiscountPercent = cart.reduce(
      (acc, item) => acc + item.product.discount,
      0,
    );
    const totalDiscount = (totalPrice * totalDiscountPercent) / 100;
    const totalDiscountedPrice = totalPrice - totalDiscount;
    const totalTax = totalDiscountedPrice * 0.088;
    const total = totalDiscountedPrice + totalTax;
    return { totalDiscountedPrice, totalTax, total };
  }

  return (
    <Layout>
      <div className={position.container}>
        <Product />
        <Price priceData={priceData} />
      </div>
    </Layout>
  );
}

export default Cart;

function Product() {
  const position = {
    container: "w-3/5",
  };
  const design = {
    heading: "font-medium text-2xl text-gray-700",
  };

  return (
    <div className={position.container}>
      <h1 className={design.heading}>Bag</h1>
      <Card />
    </div>
  );
}

function Price(props) {
  const { totalDiscountedPrice, totalTax, total } = props.priceData();
  const position = {
    container: "w-2/5",
    wrapper: "my-5",
    itemwrapper: "flex justify-between my-3",
  };
  const design = {
    heading: "font-medium text-2xl text-gray-700",
    itemwrapper: "text-gray-700",
    hr: "h-px my-5 bg-gray-200",
  };

  return (
    <div className={position.container}>
      <h2 className={design.heading}>Summary</h2>
      <div className={position.wrapper}>
        {[
          { heading: "Subtotal", value: totalDiscountedPrice },
          { heading: "Tax", value: totalTax },
          { heading: "Total", value: total },
        ].map((item) => (
          <>
            {item.heading === "Total" && <hr className={design.hr} />}
            <div
              key={item.heading}
              className={`${position.itemwrapper} ${design.itemwrapper}`}
            >
              <span>{item.heading}</span>
              <span>{item.value}</span>
            </div>
            {item.heading === "Total" && <hr className={design.hr} />}
          </>
        ))}
      </div>
      <Button text="Checkout" />
    </div>
  );
}

function Card() {
  const position = {
    container: "flex",
  };

  return (
    <div className={position.container}>
      <Image />
      <ProductInfo />
    </div>
  );
}

function Image() {
  const position = {
    container: "w-1/4 h-24",
  };

  return (
    <div className={position.container}>
      <img src="" alt="" />
    </div>
  );
}

function ProductInfo() {
  const position = {
    container: "w-4/12",
  };

  return (
    <div className={position.container}>
      <h2>Test</h2>
    </div>
  );
}
