import { useState, useEffect } from "react";
import axios from "axios";
import { Checkmark } from "react-checkmark";
import Layout from "../layout";

function Success() {
  const cart = JSON.parse(sessionStorage.getItem("cart")) || [];
  const user = JSON.parse(sessionStorage.getItem("user")) || {};
  const shipping = JSON.parse(sessionStorage.getItem("shipping")) || {};
  const payment = JSON.parse(sessionStorage.getItem("payment")) || {};
  const [userId, setUserId] = useState(null);
  const [cartId, setCartId] = useState(null);
  const [orderId, setOrderId] = useState(null);

  useEffect(() => {
    console.log(
      new Date(
        dateString
          .match(/\w{3}, (\w{3} \d{1,2})/g)
          .pop()
          .replace(",", "") +
          " " +
          new Date().getFullYear(),
      ),
    );

    axios
      .post("/user/create", user)
      .then((res) => setUserId(res.data._id))
      .catch((err) => console.log(err));
    axios
      .post(
        "/cart/create",
        cart.map((item) => {
          return { ...item, product: item.product._id };
        }),
      )
      .then((res) => setCartId(res.data._id))
      .catch((err) => console.log(err));
    axios
      .post("/order/create", {
        user: userId,
        cart: cartId,
        amount: cart.reduce((i, { price }) => i + price, 0) + shipping.price,
        delivery_date: new Date(
          new Date().getFullYear() +
            " " +
            shipping.date.match(/\w{3}, (\w{3} \d{1,2})/)[1],
        ),
      })
      .then((res) => setOrderId(res.data._id))
      .catch((err) => console.log(err));
    axios
      .post("/payment/create", {
        order: orderId,
        mode: payment.mode,
        promo: payment.promo,
        amount: cart.reduce((i, { price }) => i + price, 0) + shipping?.price,
        status: payment.mode === "Card" ? "Paid" : "Pending",
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <Layout>
      <div className="my-20">
        <Checkmark size="10rem" />
        <div className="text-center my-10 *:my-3">
          <h3 className="font-medium text-2xl">Order placed successfully!</h3>
          <h4 className="text-xl">{orderId && "ID: #" + orderId}</h4>
        </div>
        <div className="grid grid-cols-2 gap-4 w-3/4 mx-auto">
          <div className="text-center">
            <h4 className="font-medium text-lg">Your Information</h4>
            <p className="capitalize">
              {user?.first_name} {user?.last_name}
            </p>
            <p className="lowercase">{user?.email}</p>
            <p>{user?.phone.replace(/(\d{3})(\d{3})(\d{4})/, "$1-$2-$3")}</p>
          </div>
          <div className="text-center">
            <h4 className="font-medium text-lg">Payment Information</h4>
            {payment?.mode === "Cash" ? (
              <p>Cash on Delivery</p>
            ) : (
              <div>
                <p>Card Payment</p>
                <p>XXXX-XXXX-XXXX-{payment?.card_number.slice(-2)}</p>
                <p>{payment?.expiry_month + " / " + payment?.expiry_year}</p>
              </div>
            )}
          </div>
          <div className="text-center">
            <h4 className="font-medium text-lg">Delivery Information</h4>
            <p>{user?.address}</p>
          </div>
          <div className="text-center">
            <h4 className="font-medium text-lg">Shipping Information</h4>
            <p>{shipping?.date}</p>
          </div>
        </div>
        <div className="flex flex-wrap gap-5 justify-center items-center my-10 pb-20">
          {cart?.map((item) => (
            <div
              key={item.product._id}
              className="w-36 h-36 border border-gray-200 rounded-md bg-gray-100"
            >
              <a href={"/product/" + item.product.slug}>
                <img
                  className="h-full p-2 object-contain"
                  src={item.product.image}
                  alt={item.product.name}
                />
              </a>
              <p className="font-medium text-center pt-2">
                {item.product.name}
              </p>
              <p className="font-medium text-center pt-2">
                {"$" + item.price.toFixed(2)}
              </p>
            </div>
          ))}
        </div>
      </div>
    </Layout>
  );
}

export default Success;
