import { useState, useEffect } from "react";
import Layout from "../components/layout/layout";
import Bin from "../icons/bin";
import { getDiscountedPrice } from "../helper";

function Cart() {
  const [cart, setCart] = useState([]);
  const position = {
    container: "flex flex-col md:flex-row gap-16 w-9/12 m-auto",
    leftwrapper: "md:w-3/5",
    rightwrapper: "md:w-2/5",
    cardcontainer: "flex my-5",
    image: "w-1/4 p-4",
    cardwrapper: "grid grid-cols-2 grid-rows-2 gap-4 mx-4 w-full",
  };
  const design = {
    heading: "font-medium text-xl text-gray-700",
    image: "bg-gray-100 object-contain",
  };

  useEffect(() => {
    setCart(JSON.parse(sessionStorage.getItem("cart")));
  }, []);

  return (
    <Layout>
      <div className={position.container}>
        <div className={position.leftwrapper}>
          <h2 className={design.heading}>Bag</h2>
          {cart.map((item) => (
            <div className={position.cardcontainer}>
              <img
                className={`${position.image} ${design.image}`}
                src={item.product.image}
                alt={item.product.name}
              />
              <div className={position.cardwrapper}></div>
            </div>
          ))}
        </div>
        <div className={position.rightwrapper}>
          <h2 className={design.heading}>Summary</h2>
        </div>
      </div>
    </Layout>
  );
}

export default Cart;
