import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Layout from "../layout";

function Product() {
  const [product, setProduct] = useState([]);
  const { slug } = useParams();

  useEffect(() => {
    axios
      .post("/product", { slug: slug })
      .then((res) => setProduct(res.data))
      .catch((err) => console.log(err));
  }, [slug]);

  return (
    <Layout>
      {product &&
        product.map((prod) => (
          <div className="flex mt-10 gap-10 w-4/5 m-auto">
            <div className="w-3/5 p-4">
              <img
                className="w-11/12 m-auto bg-gray-100 px-5 object-contain border border-transparent rounded-xl"
                style={{ height: "70vh" }}
                src={prod.image}
                alt={prod.name}
              />
            </div>
            <div className="w-2/5 p-4 mt-5">
              <h2 className="text-2xl">{prod.name}</h2>
              <p className="text-medium text-gray-500 pt-2">
                {prod.category} Shoes
              </p>
              <p className="font-medium text-lg pt-4">${prod.price}</p>
              <div>
                {[0, 2.5, 5].map((increment, index) => (
                  <div
                    key={index}
                    className="flex flex-wrap *:w-1/5 *:border *:bg-gray-100 *:p-2"
                  >
                    <p>{5 + increment}</p>
                    <p>{5.5 + increment}</p>
                    <p>{6 + increment}</p>
                    <p>{6.5 + increment}</p>
                    <p>{7 + increment}</p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
    </Layout>
  );
}

export default Product;
