function Filter(props) {
  const position = {
    container: props.boolean ? "hidden" : "w-56 *:pb-4",
  };
  return (
    <div className={position.container}>
      <Price />
      <Discount />
      <Color />
      <Size />
    </div>
  );
}

export default Filter;

// Sub-components

function Price() {
  const position = {
    heading: "pb-2",
    itemwrapper: "flex gap-2 items-center pb-1",
  };
  const design = {
    heading: "text-lg font-medium",
  };
  const items = ["0-1000", "1000-2000", "2000-3000"];

  return (
    <div>
      <h4 className={`${position.heading} ${design.heading}`}>Price</h4>
      {items.map((item) => (
        <label key={item} className={position.itemwrapper}>
          <input type="radio" name="price" value={item} />
          <span>{item}</span>
        </label>
      ))}
    </div>
  );
}

function Discount() {
  const position = {
    heading: "pb-2",
    itemwrapper: "flex gap-2 items-center pb-1",
  };
  const design = {
    heading: "text-lg font-medium",
  };
  const items = ["0-10", "10-20", "20-30"];

  return (
    <div>
      <h4 className={`${position.heading} ${design.heading}`}>Discount</h4>
      {items.map((item) => (
        <label key={item} className={position.itemwrapper}>
          <input type="radio" name="discount" value={item} />
          <span>{item}</span>
        </label>
      ))}
    </div>
  );
}

function Color() {
  const position = {
    heading: "pb-2",
    itemwrapper: "grid grid-cols-3 gap-2 w-2/3 *:w-6 *:h-6",
  };
  const design = {
    heading: "text-lg font-medium",
    item: "border hover:border-gray-900 rounded-2xl cursor-pointer",
  };
  const items = ["green", "blue", "red", "yellow", "gray"];

  return (
    <div>
      <h4 className={`${position.heading} ${design.heading}`}>Color</h4>
      <div className={position.itemwrapper}>
        {items.map((item) => (
          <p key={item} className={`${design.item} bg-${item}-400`}></p>
        ))}
      </div>
    </div>
  );
}

function Size() {
  const position = {
    heading: "pb-2",
    itemwrapper: "grid grid-cols-2 w-2/3 gap-2 *:pt-1 *:w-10 *:h-9 *:m-auto",
  };
  const design = {
    heading: "text-lg font-medium",
    item: "border border-gray-300 text-center rounded-xl cursor-pointer hover:border-gray-900",
  };
  const items = ["5", "6", "7", "8"];

  return (
    <div>
      <h4 className={`${position.heading} ${design.heading}`}>Size</h4>
      <div className={position.itemwrapper}>
        {items.map((item) => (
          <p key={item} className={design.item}>
            {item}
          </p>
        ))}
      </div>
    </div>
  );
}
