import { useState } from "react";
import Layout from "../components/layout/layout";
import Input from "../components/input";
import Button from "../components/button";
import { createUser, createShipping, createPayment } from "../server";

function Checkout() {
  const position = {
    container: "w-2/4 m-auto",
    button: "*:w-full *:py-4 mt-5",
    separator: "mt-10 mb-4",
  };
  const design = { separator: "bg-gray-200" };
  const [show, setShow] = useState("delivery");
  const [cart, setCart] = useState(
    JSON.parse(sessionStorage.getItem("cart")) || [],
  );
  const [user, setUser] = useState(null);
  const [shipping, setShipping] = useState(null);
  const [payment, setPayment] = useState(null);
  const [data, setData] = useState({
    fname: "",
    lname: "",
    address: "",
    email: "",
    phone: "",
    date: "",
    payment: "",
    cname: "",
    cnumber: "",
    promo: "",
  });
  const [err, setErr] = useState({
    fname: "",
    lname: "",
    address: "",
    email: "",
    phone: "",
    cname: "",
    cnumber: "",
  });

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  function validate(name, value) {
    let error = false;
    let errMessage = "";
    switch (name) {
      case "fname":
        error = value.trim() === "";
        errMessage = "Please enter valid first name";
        break;
      case "lname":
        error = value.trim() === "";
        errMessage = "Please enter valid last name";
        break;
      case "address":
        error = value.trim() === "";
        errMessage = "Please enter valid address";
        break;
      case "email":
        error = !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value.trim());
        errMessage = "Please enter valid email address";
        break;
      case "phone":
        error = !/^\d{10}$/.test(value.trim());
        errMessage = "Please enter valid phone number";
        break;
      case "promo":
        error = value && !/^\d{5}$/.test(value.trim());
        errMessage = "Please enter valid promo code";
        break;
      case "cname":
        error = value.trim() === "";
        errMessage = "Please enter valid card name";
        break;
      case "cnumber":
        error = !/^\d{16}$/.test(value.trim());
        errMessage = "Please enter valid card number";
        break;
      default:
        break;
    }
    return { error, errMessage };
  }

  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.fname &&
      !errr.lname &&
      !errr.address &&
      !errr.email &&
      !errr.phone
    ) {
      setShow("shipping");
    }
    if (data.date) {
      setShow("payment");
    }
    if (data.payment === "Cash on Delivery" || (!errr.cname && !errr.cnumber)) {
      createUser(
        {
          fname: data.fname,
          lname: data.lname,
          address: data.address,
          email: data.email,
          phone: data.phone,
        },
        setUser,
      );
      createShipping(
        {
          date: new Date(data.date.split(" - ")[1]),
        },
        setShipping,
      );
      createPayment(
        {
          promo: data.promo,
          mode: data.payment,
          amount: cart.reduce((i, item) => i + item.price, 0),
        },
        setPayment,
      );
      console.log(user, shipping, payment);
    }
  }

  function date(days) {
    const today = new Date();
    const futureDate = new Date(today);
    futureDate.setDate(today.getDate() + days);
    const options = { weekday: "short", day: "numeric", month: "short" };
    return futureDate.toLocaleDateString("en-US", options);
  }

  return (
    <Layout>
      <div className={position.container}>
        <div className={show === "delivery" ? "block" : "hidden"}>
          <Title text="Delivery" />
          <Heading text="What's your name and address?" />
          <Input
            name="fname"
            value={data.fname}
            onChange={onChange}
            placeholder="First Name"
            errName={err.fname}
          />
          <Input
            name="lname"
            value={data.lname}
            onChange={onChange}
            placeholder="Last Name"
            errName={err.lname}
          />
          <Input
            name="address"
            value={data.address}
            onChange={onChange}
            placeholder="Address"
            errName={err.address}
          />
          <Heading text="What's your contact information?" />
          <Input
            name="email"
            value={data.email}
            onChange={onChange}
            placeholder="Email"
            errName={err.email}
          />
          <Input
            name="phone"
            value={data.phone}
            onChange={onChange}
            placeholder="Phone Number"
            errName={err.phone}
          />
          <div className={position.button}>
            <Button text="Continue" onClick={() => onClick("delivery")} />
          </div>
          <hr className={`${position.separator} ${design.separator}`} />
        </div>
        <div className={show === "shipping" ? "block" : "hidden"}>
          <Title text="Shipping" />
          <Heading text="When do you want the order?" />
          <Box
            text={`${date(4)} - ${date(7)}`}
            subtext="Free"
            boolean={data.date === `${date(4)} - ${date(7)}`}
            onClick={() =>
              setData((prev) => ({ ...prev, date: `${date(4)} - ${date(7)}` }))
            }
          />
          <Box
            text={`${date(7)} - ${date(10)}`}
            subtext="$50"
            boolean={data.date === `${date(7)} - ${date(10)}`}
            onClick={() =>
              setData((prev) => ({ ...prev, date: `${date(7)} - ${date(10)}` }))
            }
          />
          <div className={position.button}>
            <Button text="Continue" onClick={() => onClick("shipping")} />
          </div>
          <hr className={`${position.separator} ${design.separator}`} />
        </div>
        <div className={show === "payment" ? "block" : "hidden"}>
          <Title text="Payment" />
          <Heading text="Have a promo code?" />
          <Input
            name="promo"
            value={data.promo}
            onChange={onChange}
            placeholder="Promo"
            errName={err.promo}
          />
          <Heading text="How would you like to pay?" />
          <Box
            text="Credit or Debit Card"
            boolean={data.payment === "Credit or Debit Card"}
            onClick={() =>
              setData((prev) => ({ ...prev, payment: "Credit or Debit Card" }))
            }
          />
          <Box
            text="Cash on Delivery"
            boolean={data.payment === "Cash on Delivery"}
            onClick={() =>
              setData((prev) => ({ ...prev, payment: "Cash on Delivery" }))
            }
          />
          {data.payment === "Credit or Debit Card" && (
            <>
              <Heading text="Enter your payment details:" />
              <Input
                name="cname"
                value={data.cname}
                onChange={onChange}
                placeholder="Name on card"
                errName={err.cname}
              />
              <Input
                name="cnumber"
                value={data.cnumber}
                onChange={onChange}
                placeholder="Card number"
                errName={err.cnumber}
              />
            </>
          )}
          <div className={position.button}>
            <Button text="Place Order" onClick={() => onClick("payment")} />
          </div>
          <hr className={`${position.separator} ${design.separator}`} />
        </div>
      </div>
    </Layout>
  );
}

export default Checkout;

function Title(props) {
  const design = "font-medium text-xl text-gray-900";
  return <h3 className={design}>{props.text}</h3>;
}

function Heading(props) {
  const position = "mt-8";
  const design = "font-medium";
  return <h4 className={`${position} ${design}`}>{props.text}</h4>;
}

function Box(props) {
  const position = {
    container: "flex justify-between p-5 my-2 cursor-pointer",
  };
  const design = {
    container: `${
      props.boolean && "border-gray-700"
    } border border-2 rounded-xl hover:border-gray-700`,
    subtext: "font-light text-gray-500",
  };

  return (
    <div
      className={`${position.container} ${design.container}`}
      onClick={props.onClick}
    >
      <p>{props.text}</p>
      {props.subtext && <p className={design.subtext}>{props.subtext}</p>}
    </div>
  );
}
