import { useEffect, useState } from "react";
import { Checkmark } from "react-checkmark";
import Layout from "../components/layout/layout";
import { createOrder } from "../server";

function Success() {
  const position = { container: "my-20", heading: "w-1/4 m-auto" };
  const design = { heading: "font-medium text-xl" };
  const [order, setOrder] = useState(
    JSON.parse(sessionStorage.getItem("order")) || {},
  );

  useEffect(() => {
    const cart = JSON.parse(sessionStorage.getItem("cart"));
    const user = JSON.parse(sessionStorage.getItem("user"));
    const shipping = JSON.parse(sessionStorage.getItem("shipping"));
    const payment = JSON.parse(sessionStorage.getItem("payment"));

    !order._id &&
      createOrder(
        {
          cart: cart.map((item) => item._id),
          user: user._id,
          shipping: shipping._id,
          payment: payment._id,
        },
        setOrder,
      );
  }, [order]);

  return (
    <Layout>
      <div className={position.container}>
        <Checkmark size="10rem" />
        <h1 className={design.heading}>Order placed successfully!</h1>
        <h2 className={design.heading}>{order._id}</h2>
      </div>
    </Layout>
  );
}

export default Success;
