import { ProgressBar, Step } from "react-step-progress-bar";
import "react-step-progress-bar/styles.css";

function Progressbar(props) {
  const position = {
    container: "flex flex-col items-center ",
    wrapper: "w-8 h-8 flex items-center justify-center mb-2",
  };
  const design = { container: "text-sm", wrapper: "rounded-full" };
  return (
    <ProgressBar
      percent={props.step / props.items.length - 1 * 100}
      filledBackground="linear-gradient(to right, #4caf50, #4caf50)"
    >
      {props.items.map((item, i) => (
        <Step key={item} transition="scale">
          {({ accomplished }) => (
            <div
              className={`${position.container} ${design.container} ${
                accomplished ? "text-green-600 font-bold" : "text-gray-400"
              }`}
            >
              <div
                className={`${position.wrapper} ${design.wrapper} ${
                  accomplished
                    ? "bg-green-600 text-white"
                    : "bg-gray-200 text-gray-500"
                }`}
              >
                {i + 1}
              </div>
              <span>{item}</span>
            </div>
          )}
        </Step>
      ))}
    </ProgressBar>
  );
}

export default Progressbar;
