import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Layout from "../components/layout/layout";
import { getProduct } from "../server";

function Product() {
  const [product, setProduct] = useState({});
  const { slug } = useParams();
  const position = {
    container: "flex md:gap-10 w-11/12 m-auto",
    image: "sticky top-0 p-4",
    imagewrapper: "w-3/5 p-4",
    productwrapper: "w-2/5 p-4",
    subimage: "w-20 h-20 p-2",
    subimagewrapper: "flex gap-2",
    subsubheading: "py-5 *:px-2",
  };
  const design = {
    image: "object-contain bg-gray-100 rounded-xl",
    subheading: "text-gray-500",
    subimage: "border object-contain rounded-xl",
    subsubheading: "font-medium text-lg *:text-gray-400 *:line-through",
    heading: "text-lg md:text-2xl",
  };

  useEffect(() => {
    getProduct({ slug }, setProduct);
  }, []);

  return (
    <Layout>
      <div className={position.container}>
        <div className={position.imagewrapper}>
          <img
            className={`${position.image} ${design.image}`}
            src={product.image}
            alt={product.slug}
            style={{ height: "80vh" }}
          />
        </div>
        <div className={position.productwrapper}>
          <h1 className={design.heading}>{product.name}</h1>
          <h2 className={design.subheading}>{product.category} Shoes</h2>
          <h3 className={`${position.subsubheading} ${design.subsubheading}`}>
            ${product.price - (product.price * product.discount) / 100}
            <span>${product.price}</span>
          </h3>
          <div className={position.subimagewrapper}>
            {product.colors.map((color) => (
              <img
                className={`bg-${color}-400 ${position.subimage} ${design.subimage}`}
                src={product.image}
                alt={color}
              />
            ))}
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Product;
