import { useEffect, useState } from "react";
import Layout from "../components/layout/layout";
import Input from "../components/input";
import Button from "../components/button";

function Checkout() {
  const [data, setData] = useState({
    fname: "",
    lname: "",
    address: "",
    email: "",
    phone: "",
  });
  const position = {
    container: "w-2/4 m-auto",
    separator: "h-px my-4",
  };
  const design = {
    separator: "bg-gray-200",
    heading: "text-xl text-gray-900 font-medium",
  };

  useEffect(() => {
    console.log(data);
  }, [data]);

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  return (
    <Layout>
      <div className={position.container}>
        {[
          {
            name: "Delivery",
            component: <Delivery data={data} onChange={onChange} />,
          },
          {
            name: "Shipping",
            component: <Shipping data={data} onChange={onChange} />,
          },
          {
            name: "Payment",
            component: <Payment data={data} onChange={onChange} />,
          },
        ].map((item) => (
          <div key={item.name}>
            <h3 className={design.heading}>{item.name}</h3>
            {item.component}
            <hr className={`${position.separator} ${design.separator}`} />
          </div>
        ))}
      </div>
    </Layout>
  );
}

export default Checkout;

function Delivery(props) {
  const position = { button: "*:w-full *:p-4 my-4" };
  const items = [
    {
      name: "fname",
      value: props.data.fname,
      placeholder: "First Name",
      onChange: props.onChange,
      //errName: "Please enter your first name",
    },
    {
      name: "lname",
      value: props.data.lname,
      placeholder: "Last Name",
      onChange: props.onChange,
      //errName: "Please enter your last name",
    },
    {
      name: "address",
      value: props.data.address,
      placeholder: "Address",
      onChange: props.onChange,
      //errName: "Please enter your address",
    },
    {
      name: "email",
      value: props.data.email,
      placeholder: "Email",
      onChange: props.onChange,
      //errName: "Please enter your email",
    },
    {
      name: "phone",
      value: props.data.phone,
      placeholder: "Phone Number",
      onChange: props.onChange,
      //errName: "Please enter your phone",
    },
  ];

  return (
    <div>
      <Item heading="What's your name and address?" items={items.slice(0, 3)} />
      <Item
        heading="What's your contact information?"
        items={items.slice(3, 6)}
      />
      <div className={position.button}>
        <Button text="Continue" />
      </div>
    </div>
  );
}

function Shipping() {
  return (
    <div>
      <h1>Shipping</h1>
    </div>
  );
}

function Payment() {
  return (
    <div>
      <h1>Payment</h1>
    </div>
  );
}

function Item(props) {
  const design = "font-medium";
  const position = "mb-3 mt-8";
  return (
    <>
      <h4 className={`${position} ${design}`}>{props.heading}</h4>
      {props.items.map((item) => (
        <Input
          key={item.name}
          name={item.name}
          value={item.value}
          onChange={item.onChange}
          placeholder={item.placeholder}
          errName={item.errName}
        />
      ))}
    </>
  );
}
