import { useEffect, useState } from "react";
import LogoIcon from "./icons/logo";
import SearchIcon from "./icons/search";
import WishlistIcon from "./icons/wishlist";
import CartIcon from "./icons/cart";

function App(props) {
  return (
    <>
      <Header />
      {props.children}
      <Footer />
    </>
  );
}

function Header() {
  const [cartSize, setCartSize] = useState(0);
  useEffect(() => {
    const updateCartSize = () => {
      const cart = JSON.parse(sessionStorage.getItem("cart"));
      cart && setCartSize(cart.length);
    };
    updateCartSize();
    window.addEventListener("storage", updateCartSize);
    return () => {
      window.removeEventListener("storage", updateCartSize);
    };
  }, []);
  return (
    <>
      <div className="flex justify-end bg-gray-200 px-5 py-2">
        <ul className="flex space-x-3 text-xs font-medium">
          <li>Register</li>
          <li>|</li>
          <li>Account</li>
        </ul>
      </div>
      <div className="flex justify-between items-center border px-5 py-2">
        <a href="/">
          <LogoIcon />
        </a>
        <div className="flex justify-end items-center space-x-3 sm:space-x-6">
          <ul className="flex space-x-6">
            <li>New x Featured</li>
            <li>Men</li>
            <li>Women</li>
          </ul>
          <div className="flex space-x-2 bg-gray-100 hover:bg-gray-200 rounded-3xl p-1.5 w-1/4">
            <SearchIcon />
            <input
              className="bg-transparent outline-none w-full"
              name="search"
              placeholder="Search"
            />
          </div>
          <div className="flex space-x-1 *:rounded 2xl *:p-1.5">
            <button className="hover:bg-gray-200">
              <WishlistIcon />
            </button>
            <button className="hover:bg-gray-200">
              <CartIcon />
              <p>{cartSize && cartSize}</p>
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

function Footer() {
  return <footer></footer>;
}

export default App;
