import { useEffect, useState } from "react";
import axios from "axios";
import Layout from "../layout";
import LArrowIcon from "../icons/larrow";
import RArrowIcon from "../icons/rarrow";

function Home() {
  const [banner, setBanner] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios
      .post("/product", { id: "6606dc7ae21fbca9cdb4cd99" })
      .then((res) => setBanner(res.data))
      .catch((err) => console.log(err));
    axios
      .get("/products")
      .then((res) => setProducts(res.data))
      .catch((err) => console.log(err));
  }, []);
  return (
    <Layout>
      <div className="p-6">
        {banner &&
          banner.map((product) => (
            <div key={product._id} className="text-center">
              <div className="overflow-hidden h-3/6">
                <img
                  className="object-cover scale-150"
                  src={product.image}
                  alt="banner"
                />
              </div>
              <h2 className="text-5xl font-two uppercase tracking-tighter pt-8">
                {product.name}
              </h2>
              <p className="text-lg w-2/4 m-auto pt-3 pb-6">
                {product.description}
              </p>
              <button className="bg-gray-900 py-1.5 px-5 rounded-3xl text-white">
                Learn More
              </button>
            </div>
          ))}
        <div className="flex items-center justify-between mt-5">
          <p className="text-2xl">Discover</p>
          <div className="flex items-center space-x-3">
            <p>Shop</p>
            <button className="rounded-3xl p-2.5 bg-gray-200 hover:bg-gray-300">
              <LArrowIcon />
            </button>
            <button className="rounded-3xl p-2.5 bg-gray-200 hover:bg-gray-300">
              <RArrowIcon />
            </button>
          </div>
        </div>
        <div className="hide-scrollbar overflow-x-scroll my-5">
          <div className="flex space-x-3 h-2/5 w-3/12">
            {products &&
              products.map((product) => (
                <img
                  key={product._id}
                  className="bg-gray-100 px-5 h-full object-contain"
                  src={product.image}
                  alt={product.name}
                />
              ))}
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Home;
