  }
  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.fname &&
      !errr.lname &&
      !errr.phone &&
      !errr.street &&
