function ShopPreferences(props) {
  const position = {
    wrapper: "mt-6 ml-3",
    heading: "mb-3 mt-1 -m-3",
    button: "mt-6",
  };
  const design = { heading: "tracking-wide", text: "font-light" };
  const [data, setData] = useState({
    _id: "",
    size: "",
    gender: "",
    category: [],
    measure: "",
    user: props.user?._id || "",
  });
  const sizes = [
    { name: "5", value: 5 },
    { name: "5.5", value: 5.5 },
    { name: "6", value: 6 },
    { name: "6.5", value: 6.5 },
    { name: "7", value: 7 },
    { name: "7.5", value: 7.5 },
    { name: "8", value: 8 },
    { name: "8.5", value: 8.5 },
    { name: "9", value: 9 },
    { name: "9.5", value: 9.5 },
    { name: "10", value: 10 },
    { name: "10.5", value: 10.5 },
    { name: "11", value: 11 },
    { name: "11.5", value: 11.5 },
    { name: "12", value: 12 },
  ];
  const [boolean, setBoolean] = useState(false);

  useEffect(() => {
    if (props.user && props.user._id) {
      getPreferences({ user: props.user._id }, (res) => {
        typeof res === "object" &&
          setData({
            _id: res._id || "",
            size: res.size || "",
            gender: res.gender || "",
            category: res.category || [],
            measure: res.measure || "",
            user: props.user._id,
          });
        typeof res === "object" && res._id && setBoolean(true);
      });
    }
  }, [props.user]);

  function onChange(e) {
    const { name, value, checked } = e.target;
    if (name === "category") {
      setData((prev) => ({
        ...prev,
        category: checked
          ? [...prev.category, value]
          : prev.category.filter((item) => item !== value),
      }));
    } else {
      setData((prev) => ({ ...prev, [name]: value }));
    }
  }

  function onClick() {
    boolean
      ? updatePreferences(data, (res) => {
          setData({
            _id: res._id || "",
            size: res.size || "",
            gender: res.gender || "",
            category: res.category || [],
            measure: res.measure || "",
            user: props.user._id,
          });
        })
      : createPreferences({
          size: data.size || "",
          gender: data.gender || "",
          category: data.category || [],
          measure: data.measure || "",
          user: data.user || "",
        });
  }

  return (
    <>
      <Select
        name="size"
        placeholder="Shoe Size*"
        value={data.size}
        onChange={onChange}
        options={sizes}
      />
      <div className={position.wrapper}>
        <h3 className={`${position.heading} ${design.heading}`}>
          Shopping Preferences
        </h3>
        <Radio
          className={design.text}
          name="gender"
          options={["Men's", "Women's"]}
          onChange={onChange}
          checked={data.gender}
        />
      </div>
      <div className={position.wrapper}>
        <h3 className={`${position.heading} ${design.heading}`}>
          Additional Preferences
        </h3>
        <Checkbox
          className={design.text}
          name="category"
          options={["Men's", "Women's", "Girls'", "Boys'"]}
          onChange={onChange}
          data={data.category}
        />
      </div>
      <div className={position.wrapper}>
        <h3 className={`${position.heading} ${design.heading}`}>
          Unit of Measure
        </h3>
        <Radio
          className={design.text}
          name="measure"
          options={["Metric", "Imperial"]}
          onChange={onChange}
          checked={data.measure}
        />
      </div>
      <div className={position.button}>
        <Button text="Save" onClick={onClick} />
      </div>
    </>
  );
}

function CommunicationPreferences(props) {
  const position = { heading: "mb-3 mt-1", text: "mb-3", button: "mt-6" };
