  const [data, setData] = useState({
    _id: "",
    communication: "",
    user: props.user?._id || "",
  });
  const [boolean, setBoolean] = useState(false);

  useEffect(() => {
    if (props.user && props.user._id) {
      getPreferences({ user: props.user._id }, (res) => {
        typeof res === "object" &&
          setData({
            _id: res._id || "",
            communication: res.communication || "",
          });
        typeof res === "object" && res._id && setBoolean(true);
      });
    }
  }, [props.user]);

  function onChange(e) {
    const { value } = e.target;
    setData((prev) => ({
      ...prev,
      communication: prev.communication === value ? "" : value,
    }));
  }

  function onClick() {
    boolean
      ? updatePreferences(data, (res) =>
          setData({ _id: res._id, communication: res.communication }),
        )
      : createPreferences(data);
