import React from "react";
import { ProgressBar, Step } from "react-step-progress-bar";
import "react-step-progress-bar/styles.css";

function Progressbar({ items, step }) {
  const position = {
    container: "flex flex-col items-center",
    wrapper: "w-4 h-4 mb-2",
  };
  const design = { container: "text-sm" };

  return (
    <ProgressBar
      percent={(step / (items.length - 1)) * 100}
      filledBackground="linear-gradient(to right, #4caf50, #4caf50)"
    >
      {items.map((item, index) => (
        <Step key={index} transition="scale">
          {({ accomplished }) => (
            <div
              className={`${position.container} ${design.container} ${
                accomplished ? "text-green-600 font-bold" : "text-gray-400"
              }`}
            >
              <div
                className={`${position.wrapper} ${
                  accomplished ? "bg-green-600" : "bg-gray-200"
                }`}
              ></div>
              <span>{item}</span>
            </div>
          )}
        </Step>
      ))}
    </ProgressBar>
  );
}

export default Progressbar;
