import axios from "axios";

function getProduct(identifier, setProduct) {
  axios
    .post("/product", identifier)
    .then((res) => setProduct(res.data[0]))
    .catch((err) => console.log(err));
}

function getProducts(identifier, setProducts) {
  axios
    .post("/products", identifier)
    .then((res) => setProducts(res.data))
    .catch((err) => console.log(err));
}

function createCart(data, callback) {
  const cart = JSON.parse(sessionStorage.getItem("cart")) || [];
  axios
    .post("/cart/create", data)
    .then((res) =>
      sessionStorage.setItem("cart", JSON.stringify([...cart, res.data])),
    )
    .catch((err) => console.log(err))
    .finally(() => callback());
}

function deleteCart(data) {
  const cart = JSON.parse(sessionStorage.getItem("cart")) || [];
  const filteredCart = cart.filter((item) => item._id !== data._id);
  axios
    .post("/cart/delete", data)
    .then(() => sessionStorage.setItem("cart", JSON.stringify(filteredCart)))
    .catch((err) => console.log(err));
}

function createUser(data) {
  axios.post("/user/create", data).catch((err) => console.log(err));
}

function postShipping(identifier, setShipping) {
  axios
    .post("/shipping/create", identifier)
    .then((res) => setShipping(res.data[0]))
    .catch((err) => console.log(err));
}

function postPayment(identifier, setPayment) {
  axios
    .post("/payment/create", identifier)
    .then((res) => setPayment(res.data[0]))
    .catch((err) => console.log(err));
}

export { getProduct, getProducts, createCart, deleteCart, createUser };
